05683         protected int GetUTF16(string text, int i)
05684         {
05685             int unicode = 0;
05686             unicode += HexToInt(text[i]) << 12;
05687             unicode += HexToInt(text[i + 1]) << 8;
05688             unicode += HexToInt(text[i + 2]) << 4;
05689             unicode += HexToInt(text[i + 3]);
05690             return unicode;
05691         }
