05719             unicode += HexToInt(text[i + 2]) << 20;
05720             unicode += HexToInt(text[i + 3]) << 16;
05721             unicode += HexToInt(text[i + 4]) << 12;
05722             unicode += HexToInt(text[i + 5]) << 8;
05723             unicode += HexToInt(text[i + 6]) << 4;
