05754         protected Color32 HexCharsToColor(char[] hexChars, int tagCount)
05755         {
05756             if (tagCount == 4)
05757             {
05758                 byte r = (byte)(HexToInt(hexChars[1]) * 16 + HexToInt(hexChars[1]));
05759                 byte g = (byte)(HexToInt(hexChars[2]) * 16 + HexToInt(hexChars[2]));
05760                 byte b = (byte)(HexToInt(hexChars[3]) * 16 + HexToInt(hexChars[3]));
05761 
05762                 return new Color32(r, g, b, 255);
05763             }
05764             else if (tagCount == 5)
05765             {
05766                 byte r = (byte)(HexToInt(hexChars[1]) * 16 + HexToInt(hexChars[1]));
05767                 byte g = (byte)(HexToInt(hexChars[2]) * 16 + HexToInt(hexChars[2]));
05768                 byte b = (byte)(HexToInt(hexChars[3]) * 16 + HexToInt(hexChars[3]));
05769                 byte a = (byte)(HexToInt(hexChars[4]) * 16 + HexToInt(hexChars[4]));
05770 
05771                 return new Color32(r, g, b, a);
05772             }
05773             else if (tagCount == 7)
05774             {
05775                 byte r = (byte)(HexToInt(hexChars[1]) * 16 + HexToInt(hexChars[2]));
05776                 byte g = (byte)(HexToInt(hexChars[3]) * 16 + HexToInt(hexChars[4]));
05777                 byte b = (byte)(HexToInt(hexChars[5]) * 16 + HexToInt(hexChars[6]));
05778 
05779                 return new Color32(r, g, b, 255);
05780             }
05781             else if (tagCount == 9)
05782             {
05783                 byte r = (byte)(HexToInt(hexChars[1]) * 16 + HexToInt(hexChars[2]));
05784                 byte g = (byte)(HexToInt(hexChars[3]) * 16 + HexToInt(hexChars[4]));
05785                 byte b = (byte)(HexToInt(hexChars[5]) * 16 + HexToInt(hexChars[6]));
05786                 byte a = (byte)(HexToInt(hexChars[7]) * 16 + HexToInt(hexChars[8]));
05787 
05788                 return new Color32(r, g, b, a);
05789             }
05790             else if (tagCount == 10)
05791             {
05792                 byte r = (byte)(HexToInt(hexChars[7]) * 16 + HexToInt(hexChars[7]));
05793                 byte g = (byte)(HexToInt(hexChars[8]) * 16 + HexToInt(hexChars[8]));
05794                 byte b = (byte)(HexToInt(hexChars[9]) * 16 + HexToInt(hexChars[9]));
05795 
05796                 return new Color32(r, g, b, 255);
05797             }
05798             else if (tagCount == 11)
05799             {
05800                 byte r = (byte)(HexToInt(hexChars[7]) * 16 + HexToInt(hexChars[7]));
05801                 byte g = (byte)(HexToInt(hexChars[8]) * 16 + HexToInt(hexChars[8]));
05802                 byte b = (byte)(HexToInt(hexChars[9]) * 16 + HexToInt(hexChars[9]));
05803                 byte a = (byte)(HexToInt(hexChars[10]) * 16 + HexToInt(hexChars[10]));
05804 
05805                 return new Color32(r, g, b, a);
05806             }
05807             else if (tagCount == 13)
05808             {
05809                 byte r = (byte)(HexToInt(hexChars[7]) * 16 + HexToInt(hexChars[8]));
05810                 byte g = (byte)(HexToInt(hexChars[9]) * 16 + HexToInt(hexChars[10]));
05811                 byte b = (byte)(HexToInt(hexChars[11]) * 16 + HexToInt(hexChars[12]));
05812 
05813                 return new Color32(r, g, b, 255);
05814             }
05815             else if (tagCount == 15)
05816             {
05817                 byte r = (byte)(HexToInt(hexChars[7]) * 16 + HexToInt(hexChars[8]));
05818                 byte g = (byte)(HexToInt(hexChars[9]) * 16 + HexToInt(hexChars[10]));
05819                 byte b = (byte)(HexToInt(hexChars[11]) * 16 + HexToInt(hexChars[12]));
05820                 byte a = (byte)(HexToInt(hexChars[13]) * 16 + HexToInt(hexChars[14]));
05821 
05822                 return new Color32(r, g, b, a);
05823             }
05824 
05825             return new Color32(255, 255, 255, 255);
05826         }
