05836         protected Color32 HexCharsToColor(char[] hexChars, int startIndex, int length)
05837         {
05838             if (length == 7)
05839             {
05840                 byte r = (byte)(HexToInt(hexChars[startIndex + 1]) * 16 + HexToInt(hexChars[startIndex + 2]));
05841                 byte g = (byte)(HexToInt(hexChars[startIndex + 3]) * 16 + HexToInt(hexChars[startIndex + 4]));
05842                 byte b = (byte)(HexToInt(hexChars[startIndex + 5]) * 16 + HexToInt(hexChars[startIndex + 6]));
05843 
05844                 return new Color32(r, g, b, 255);
05845             }
05846             else if (length == 9)
05847             {
05848                 byte r = (byte)(HexToInt(hexChars[startIndex + 1]) * 16 + HexToInt(hexChars[startIndex + 2]));
05849                 byte g = (byte)(HexToInt(hexChars[startIndex + 3]) * 16 + HexToInt(hexChars[startIndex + 4]));
05850                 byte b = (byte)(HexToInt(hexChars[startIndex + 5]) * 16 + HexToInt(hexChars[startIndex + 6]));
05851                 byte a = (byte)(HexToInt(hexChars[startIndex + 7]) * 16 + HexToInt(hexChars[startIndex + 8]));
05852 
05853                 return new Color32(r, g, b, a);
05854             }
05855 
05856             return s_colorWhite;
05857         }
