05868         int GetAttributeParameters(char[] chars, int startIndex, int length, ref float[] parameters)
05869         {
05870             int endIndex = startIndex;
05871             int attributeCount = 0;
05872 
05873             while (endIndex < startIndex + length)
05874             {
05875                 parameters[attributeCount] = ConvertToFloat(chars, startIndex, length, out endIndex);
05876 
05877                 length -= (endIndex - startIndex) + 1;
05878                 startIndex = endIndex + 1;
05879 
05880                 attributeCount += 1;
05881             }
05882 
05883             return attributeCount;
05884         }
