05894         protected float ConvertToFloat(char[] chars, int startIndex, int length)
05895         {
05896             int lastIndex = 0;
05897             return ConvertToFloat(chars, startIndex, length, out lastIndex);
05898         }
05899 
05900 
05909         protected float ConvertToFloat(char[] chars, int startIndex, int length, out int lastIndex)
05910         {
05911             if (startIndex == 0) { lastIndex = 0; return -9999; }
05912             int endIndex = startIndex + length;
05913 
05914             bool isIntegerValue = true;
05915             float decimalPointMultiplier = 0;
05916 
05917             // Set value multiplier checking the first character to determine if we are using '+' or '-'
05918             int valueSignMultiplier = 1;
05919             if (chars[startIndex] == '+')
05920             {
05921                 valueSignMultiplier = 1;
05922                 startIndex += 1;
05923             }
05924             else if (chars[startIndex] == '-')
05925             {
05926                 valueSignMultiplier = -1;
05927                 startIndex += 1;
05928             }
05929 
05930             float value = 0;
05931 
05932             for (int i = startIndex; i < endIndex; i++)
05933             {
05934                 uint c = chars[i];
05935 
05936                 if (c >= '0' && c <= '9' || c == '.')
05937                 {
05938                     if (c == '.')
05939                     {
05940                         isIntegerValue = false;
05941                         decimalPointMultiplier = 0.1f;
05942                         continue;
05943                     }
05944 
05945                     //Calculate integer and floating point value
05946                     if (isIntegerValue)
05947                         value = value * 10 + (c - 48) * valueSignMultiplier;
05948                     else
05949                 {
05950                         value = value + (c - 48) * decimalPointMultiplier * valueSignMultiplier;
05951                         decimalPointMultiplier *= 0.1f;
05952                 }
05953 
05954                     continue;
05955                 }
05956                 else if (c == ',')
05957                 {
05958                     if (i + 1 < endIndex && chars[i + 1] == ' ')
05959                         lastIndex = i + 1;
05960                 else
05961                         lastIndex = i;
05962 
05963                     return value;
05964                 }
05965             }
05966 
05967             lastIndex = endIndex;
05968             return value;
05969         }
05970 
05971 
05979         protected bool ValidateHtmlTag(int[] chars, int startIndex, out int endIndex)
05980         {
05981             int tagCharCount = 0;
05982             byte attributeFlag = 0;
05983 
05984             TagUnits tagUnits = TagUnits.Pixels;
05985             TagType tagType = TagType.None;
05986 
05987             int attributeIndex = 0;
05988             m_xmlAttribute[attributeIndex].nameHashCode = 0;
05989             m_xmlAttribute[attributeIndex].valueType = TagType.None;
05990             m_xmlAttribute[attributeIndex].valueHashCode = 0;
05991             m_xmlAttribute[attributeIndex].valueStartIndex = 0;
05992             m_xmlAttribute[attributeIndex].valueLength = 0;
05993 
05994             // Clear attribute name hash codes
05995             m_xmlAttribute[1].nameHashCode = 0;
05996             m_xmlAttribute[2].nameHashCode = 0;
05997             m_xmlAttribute[3].nameHashCode = 0;
05998             m_xmlAttribute[4].nameHashCode = 0;
05999 
06000             endIndex = startIndex;
06001             bool isTagSet = false;
06002             bool isValidHtmlTag = false;
06003 
06004             for (int i = startIndex; i < chars.Length && chars[i] != 0 && tagCharCount < m_htmlTag.Length && chars[i] != 60; i++)
06005             {
06006                 if (chars[i] == 62) // ASCII Code of End HTML tag '>'
06007                 {
06008                     isValidHtmlTag = true;
06009                     endIndex = i;
06010                     m_htmlTag[tagCharCount] = (char)0;
06011                     break;
06012                 }
06013 
06014                 m_htmlTag[tagCharCount] = (char)chars[i];
06015                 tagCharCount += 1;
06016 
06017                 if (attributeFlag == 1)
06018                 {
06019                     if (tagType == TagType.None)
06020                     {
06021                         // Check for attribute type
06022                         if (chars[i] == 43 || chars[i] == 45 || chars[i] == 46 || (chars[i] >= '0' && chars[i] <= '9'))
06023                         {
06024                             tagType = TagType.NumericalValue;
06025                             m_xmlAttribute[attributeIndex].valueType = TagType.NumericalValue;
06026                             m_xmlAttribute[attributeIndex].valueStartIndex = tagCharCount - 1;
06027                             m_xmlAttribute[attributeIndex].valueLength += 1;
06028                         }
06029                         else if (chars[i] == 35)
06030                         {
06031                             tagType = TagType.ColorValue;
06032                             m_xmlAttribute[attributeIndex].valueType = TagType.ColorValue;
06033                             m_xmlAttribute[attributeIndex].valueStartIndex = tagCharCount - 1;
06034                             m_xmlAttribute[attributeIndex].valueLength += 1;
06035                         }
06036                         else if (chars[i] == 34)
06037                         {
06038                             tagType = TagType.StringValue;
06039                             m_xmlAttribute[attributeIndex].valueType = TagType.StringValue;
06040                             m_xmlAttribute[attributeIndex].valueStartIndex = tagCharCount;
06041                         }
06042                         else
06043                         {
06044                             tagType = TagType.StringValue;
06045                             m_xmlAttribute[attributeIndex].valueType = TagType.StringValue;
06046                             m_xmlAttribute[attributeIndex].valueStartIndex = tagCharCount - 1;
06047                             m_xmlAttribute[attributeIndex].valueHashCode = (m_xmlAttribute[attributeIndex].valueHashCode << 5) + m_xmlAttribute[attributeIndex].valueHashCode ^ chars[i];
06048                             m_xmlAttribute[attributeIndex].valueLength += 1;
06049                         }
06050                     }
06051                     else
06052                     {
06053                         if (tagType == TagType.NumericalValue)
06054                         {
06055                             // Check for termination of numerical value.
06056                             if (chars[i] == 112 || chars[i] == 101 || chars[i] == 37 || chars[i] == 32)
06057                             {
06058                                 attributeFlag = 2;
06059                                 tagType = TagType.None;
06060                                 attributeIndex += 1;
06061                                 m_xmlAttribute[attributeIndex].nameHashCode = 0;
06062                                 m_xmlAttribute[attributeIndex].valueType = TagType.None;
06063                                 m_xmlAttribute[attributeIndex].valueHashCode = 0;
06064                                 m_xmlAttribute[attributeIndex].valueStartIndex = 0;
06065                                 m_xmlAttribute[attributeIndex].valueLength = 0;
06066 
06067                                 if (chars[i] == 101)
06068                                     tagUnits = TagUnits.FontUnits;
06069                                 else if (chars[i] == 37)
06070                                     tagUnits = TagUnits.Percentage;
06071                             }
06072                             else if (attributeFlag != 2)
06073                             {
06074                                 m_xmlAttribute[attributeIndex].valueLength += 1;
06075                             }
06076                         }
06077                         else if (tagType == TagType.ColorValue)
06078                         {
06079                             if (chars[i] != 32)
06080                             {
06081                                 m_xmlAttribute[attributeIndex].valueLength += 1;
06082                             }
06083                             else
06084                             {
06085                                 attributeFlag = 2;
06086                                 tagType = TagType.None;
06087                                 attributeIndex += 1;
06088                                 m_xmlAttribute[attributeIndex].nameHashCode = 0;
06089                                 m_xmlAttribute[attributeIndex].valueType = TagType.None;
06090                                 m_xmlAttribute[attributeIndex].valueHashCode = 0;
06091                                 m_xmlAttribute[attributeIndex].valueStartIndex = 0;
06092                                 m_xmlAttribute[attributeIndex].valueLength = 0;
06093                             }
06094                         }
06095                         else if (tagType == TagType.StringValue)
06096                         {
06097                             // Compute HashCode value for the named tag.
06098                             if (chars[i] != 34)
06099                             {
06100                                 m_xmlAttribute[attributeIndex].valueHashCode = (m_xmlAttribute[attributeIndex].valueHashCode << 5) + m_xmlAttribute[attributeIndex].valueHashCode ^ chars[i];
06101                                 m_xmlAttribute[attributeIndex].valueLength += 1;
06102                             }
06103                             else
06104                             {
06105                                 //m_xmlAttribute[attributeIndex].valueHashCode = -1;
06106                                 attributeFlag = 2;
06107                                 tagType = TagType.None;
06108                                 attributeIndex += 1;
06109                                 m_xmlAttribute[attributeIndex].nameHashCode = 0;
06110                                 m_xmlAttribute[attributeIndex].valueType = TagType.None;
06111                                 m_xmlAttribute[attributeIndex].valueHashCode = 0;
06112                                 m_xmlAttribute[attributeIndex].valueStartIndex = 0;
06113                                 m_xmlAttribute[attributeIndex].valueLength = 0;
06114                             }
06115                         }
06116                     }
06117                 }
06118 
06119 
06120                 if (chars[i] == 61) // '=' 
06121                     attributeFlag = 1;
06122 
06123                 // Compute HashCode for the name of the attribute
06124                 if (attributeFlag == 0 && chars[i] == 32)
06125                 {
06126                     if (isTagSet) return false;
06127 
06128                     isTagSet = true;
06129                     attributeFlag = 2;
06130 
06131                     tagType = TagType.None;
06132                     attributeIndex += 1;
06133                     m_xmlAttribute[attributeIndex].nameHashCode = 0;
06134                     m_xmlAttribute[attributeIndex].valueType = TagType.None;
06135                     m_xmlAttribute[attributeIndex].valueHashCode = 0;
06136                     m_xmlAttribute[attributeIndex].valueStartIndex = 0;
06137                     m_xmlAttribute[attributeIndex].valueLength = 0;
06138                 }
06139 
06140                 if (attributeFlag == 0)
06141                     m_xmlAttribute[attributeIndex].nameHashCode = (m_xmlAttribute[attributeIndex].nameHashCode << 3) - m_xmlAttribute[attributeIndex].nameHashCode + chars[i];
06142 
06143                 if (attributeFlag == 2 && chars[i] == 32)
06144                     attributeFlag = 0;
06145 
06146             }
06147 
06148             if (!isValidHtmlTag)
06149             {
06150                 return false;
06151             }
06152 
06153             //Debug.Log("Tag is [" + m_htmlTag.ArrayToString() + "].  Tag HashCode: " + m_xmlAttribute[0].nameHashCode + "  Tag Value HashCode: " + m_xmlAttribute[0].valueHashCode + "  Attribute 1 HashCode: " + m_xmlAttribute[1].nameHashCode + " Value HashCode: " + m_xmlAttribute[1].valueHashCode);
06154             //for (int i = 0; i < attributeIndex + 1; i++)
06155             //    Debug.Log("Tag [" + i + "] with HashCode: " + m_xmlAttribute[i].nameHashCode + " has value of [" + new string(m_htmlTag, m_xmlAttribute[i].valueStartIndex, m_xmlAttribute[i].valueLength) + "] Numerical Value: " + ConvertToFloat(m_htmlTag, m_xmlAttribute[i].valueStartIndex, m_xmlAttribute[i].valueLength));
06156 
06157 
06158             // Special handling of the no parsing tag </noparse> </NOPARSE> tag
06159             if (tag_NoParsing && (m_xmlAttribute[0].nameHashCode != 53822163 && m_xmlAttribute[0].nameHashCode != 49429939))
06160                 return false;
06161             else if (m_xmlAttribute[0].nameHashCode == 53822163 || m_xmlAttribute[0].nameHashCode == 49429939)
06162             {
06163                 tag_NoParsing = false;
06164                 return true;
06165             }
06166 
06167             // Color <#FFF> 3 Hex values (short form)
06168             if (m_htmlTag[0] == 35 && tagCharCount == 4)
06169             {
06170                 m_htmlColor = HexCharsToColor(m_htmlTag, tagCharCount);
06171                 m_colorStack.Add(m_htmlColor);
06172                 return true;
06173             }
06174             // Color <#FFF7> 4 Hex values with alpha (short form)
06175             else if (m_htmlTag[0] == 35 && tagCharCount == 5)
06176             {
06177                 m_htmlColor = HexCharsToColor(m_htmlTag, tagCharCount);
06178                 m_colorStack.Add(m_htmlColor);
06179                 return true;
06180             }
06181             // Color <#FF00FF>
06182             else if (m_htmlTag[0] == 35 && tagCharCount == 7) // if Tag begins with # and contains 7 characters. 
06183             {
06184                 m_htmlColor = HexCharsToColor(m_htmlTag, tagCharCount);
06185                 m_colorStack.Add(m_htmlColor);
06186                 return true;
06187             }
06188             // Color <#FF00FF00> with alpha
06189             else if (m_htmlTag[0] == 35 && tagCharCount == 9) // if Tag begins with # and contains 9 characters. 
06190             {
06191                 m_htmlColor = HexCharsToColor(m_htmlTag, tagCharCount);
06192                 m_colorStack.Add(m_htmlColor);
06193                 return true;
06194             }
06195             else
06196             {
06197                 float value = 0;
06198 
06199                 switch (m_xmlAttribute[0].nameHashCode)
06200                 {
06201                     case 98: // <b>
06202                     case 66: // <B>
06203                         m_style |= FontStyles.Bold;
06204                         m_fontStyleStack.Add(FontStyles.Bold);
06205 
06206                         m_fontWeightInternal = 700;
06207                         m_fontWeightStack.Add(700);
06208                         return true;
06209                     case 427: // </b>
06210                     case 395: // </B>
06211                         if ((m_fontStyle & FontStyles.Bold) != FontStyles.Bold)
06212                         {
