00071         public static int GetCursorIndexFromPosition(TMP_Text textComponent, Vector3 position, Camera camera)
00072         {
00073             int index = TMP_TextUtilities.FindNearestCharacter(textComponent, position, camera, false);
00074 
00075             RectTransform rectTransform = textComponent.rectTransform;
00076 
00077             // Convert position into Worldspace coordinates
00078             ScreenPointToWorldPointInRectangle(rectTransform, position, camera, out position);
00079 
00080             TMP_CharacterInfo cInfo = textComponent.textInfo.characterInfo[index];
00081 
00082             // Get Bottom Left and Top Right position of the current character
00083             Vector3 bl = rectTransform.TransformPoint(cInfo.bottomLeft);
00084             Vector3 tr = rectTransform.TransformPoint(cInfo.topRight);
00085 
00086             float insertPosition = (position.x - bl.x) / (tr.x - bl.x);
00087 
00088             if (insertPosition < 0.5f)
00089                 return index;
00090             else
00091                 return index + 1;
00092 
00093         }
