00142         public static int GetCursorIndexFromPosition(TMP_Text textComponent, Vector3 position, Camera camera, out CaretPosition cursor)
00143         {
00144             int line = TMP_TextUtilities.FindNearestLine(textComponent, position, camera);
00145 
00146             int index = FindNearestCharacterOnLine(textComponent, position, line, camera, false);
00147 
00148             // Special handling if line contains only one character.
00149             if (textComponent.textInfo.lineInfo[line].characterCount == 1)
00150             {
00151                 cursor = CaretPosition.Left;
00152                 return index;
00153             }
00154 
00155             RectTransform rectTransform = textComponent.rectTransform;
00156 
00157             // Convert position into Worldspace coordinates
00158             ScreenPointToWorldPointInRectangle(rectTransform, position, camera, out position);
00159 
00160             TMP_CharacterInfo cInfo = textComponent.textInfo.characterInfo[index];
00161 
00162             // Get Bottom Left and Top Right position of the current character
00163             Vector3 bl = rectTransform.TransformPoint(cInfo.bottomLeft);
00164             Vector3 tr = rectTransform.TransformPoint(cInfo.topRight);
00165 
00166             float insertPosition = (position.x - bl.x) / (tr.x - bl.x);
00167 
00168             if (insertPosition < 0.5f)
00169             {
00170                 cursor = CaretPosition.Left;
00171                 return index;
00172             }
00173             else
00174             {
00175                 cursor = CaretPosition.Right;
00176                 return index;
00177             }
00178         }
