00180 
00188         public static int FindNearestLine(TMP_Text text, Vector3 position, Camera camera)
00189         {
00190             RectTransform rectTransform = text.rectTransform;
00191 
00192             float distance = Mathf.Infinity;
00193             int closest = -1;
00194 
00195             // Convert position into Worldspace coordinates
00196             ScreenPointToWorldPointInRectangle(rectTransform, position, camera, out position);
00197 
00198             for (int i = 0; i < text.textInfo.lineCount; i++)
00199             {
00200                 TMP_LineInfo lineInfo = text.textInfo.lineInfo[i];
00201 
00202                 float ascender = rectTransform.TransformPoint(new Vector3(0, lineInfo.ascender, 0)).y;
00203                 float descender = rectTransform.TransformPoint(new Vector3(0, lineInfo.descender, 0)).y;
00204 
00205                 if (ascender > position.y && descender < position.y)
00206                 { 
00207                     //Debug.Log("Position is on line " + i);
00208                     return i;
00209                 }
00210 
00211                 float d0 = Mathf.Abs(ascender - position.y);
00212                 float d1 = Mathf.Abs(descender - position.y);
00213 
00214                 float d = Mathf.Min(d0, d1);
00215                 if (d < distance)
00216                 {
00217                     distance = d;
00218                     closest = i;
00219                 }
00220             }
00221 
00222             //Debug.Log("Closest line to position is " + closest);
00223             return closest;
00224         }
00225 
00226 
00235         public static int FindNearestCharacterOnLine(TMP_Text text, Vector3 position, int line, Camera camera, bool visibleOnly)
00236         {
00237             RectTransform rectTransform = text.rectTransform;
00238 
00239             // Convert position into Worldspace coordinates
00240             ScreenPointToWorldPointInRectangle(rectTransform, position, camera, out position);
00241 
00242             int firstCharacter = text.textInfo.lineInfo[line].firstCharacterIndex;
00243             int lastCharacter = text.textInfo.lineInfo[line].lastCharacterIndex;
00244 
00245             float distanceSqr = Mathf.Infinity;
00246             int closest = lastCharacter;
00247 
00248             for (int i = firstCharacter; i < lastCharacter; i++)
00249             {
00250                 // Get current character info.
00251                 TMP_CharacterInfo cInfo = text.textInfo.characterInfo[i];
00252                 if (visibleOnly && !cInfo.isVisible) continue;
00253 
00254                 // Get Bottom Left and Top Right position of the current character
00255                 Vector3 bl = rectTransform.TransformPoint(cInfo.bottomLeft);
00256                 Vector3 tl = rectTransform.TransformPoint(new Vector3(cInfo.bottomLeft.x, cInfo.topRight.y, 0));
00257                 Vector3 tr = rectTransform.TransformPoint(cInfo.topRight);
00258                 Vector3 br = rectTransform.TransformPoint(new Vector3(cInfo.topRight.x, cInfo.bottomLeft.y, 0));
00259 
00260                 if (PointIntersectRectangle(position, bl, tl, tr, br))
00261                 {
00262                     closest = i;
00263                     break;
00264                 }
00265 
00266                 // Find the closest corner to position.
00267                 float dbl = DistanceToLine(bl, tl, position);
00268                 float dtl = DistanceToLine(tl, tr, position);
00269                 float dtr = DistanceToLine(tr, br, position);
00270                 float dbr = DistanceToLine(br, bl, position);
00271 
00272                 float d = dbl < dtl ? dbl : dtl;
00273                 d = d < dtr ? d : dtr;
00274                 d = d < dbr ? d : dbr;
00275 
00276                 if (distanceSqr > d)
00277                 {
00278                     distanceSqr = d;
00279                     closest = i;
00280                 }
00281             }
00282             return closest;
00283         }
00284 
00285 
00293         public static bool IsIntersectingRectTransform(RectTransform rectTransform, Vector3 position, Camera camera)
00294         {
00295             // Convert position into Worldspace coordinates
00296             ScreenPointToWorldPointInRectangle(rectTransform, position, camera, out position);
00297 
00298             rectTransform.GetWorldCorners(m_rectWorldCorners);
00299 
00300             if (PointIntersectRectangle(position, m_rectWorldCorners[0], m_rectWorldCorners[1], m_rectWorldCorners[2], m_rectWorldCorners[3]))
00301             {
00302                 return true;
00303             }
00304 
00305             return false;
00306         }
