00319         public static int FindIntersectingCharacter(TMP_Text text, Vector3 position, Camera camera, bool visibleOnly)
00320         {
00321             RectTransform rectTransform = text.rectTransform;
00322 
00323             // Convert position into Worldspace coordinates
00324             ScreenPointToWorldPointInRectangle(rectTransform, position, camera, out position);
00325 
00326             for (int i = 0; i < text.textInfo.characterCount; i++)
00327             {
00328                 // Get current character info.
00329                 TMP_CharacterInfo cInfo = text.textInfo.characterInfo[i];
00330                 if (visibleOnly && !cInfo.isVisible) continue;
00331 
00332                 // Get Bottom Left and Top Right position of the current character
00333                 Vector3 bl = rectTransform.TransformPoint(cInfo.bottomLeft);
00334                 Vector3 tl = rectTransform.TransformPoint(new Vector3(cInfo.bottomLeft.x, cInfo.topRight.y, 0));
00335                 Vector3 tr = rectTransform.TransformPoint(cInfo.topRight);
00336                 Vector3 br = rectTransform.TransformPoint(new Vector3(cInfo.topRight.x, cInfo.bottomLeft.y, 0));
00337 
00338                 if (PointIntersectRectangle(position, bl, tl, tr, br))
00339                     return i;
00340 
00341             }
00342             return -1;
00343         }
