00391         public static int FindNearestCharacter(TMP_Text text, Vector3 position, Camera camera, bool visibleOnly)
00392         {
00393             RectTransform rectTransform = text.rectTransform;
00394 
00395             float distanceSqr = Mathf.Infinity;
00396             int closest = 0;
00397 
00398             // Convert position into Worldspace coordinates
00399             ScreenPointToWorldPointInRectangle(rectTransform, position, camera, out position);
00400 
00401             for (int i = 0; i < text.textInfo.characterCount; i++)
00402             {
00403                 // Get current character info.
00404                 TMP_CharacterInfo cInfo = text.textInfo.characterInfo[i];
00405                 if (visibleOnly && !cInfo.isVisible) continue;
00406 
00407                 // Get Bottom Left and Top Right position of the current character
00408                 Vector3 bl = rectTransform.TransformPoint(cInfo.bottomLeft);
00409                 Vector3 tl = rectTransform.TransformPoint(new Vector3(cInfo.bottomLeft.x, cInfo.topRight.y, 0));
00410                 Vector3 tr = rectTransform.TransformPoint(cInfo.topRight);
00411                 Vector3 br = rectTransform.TransformPoint(new Vector3(cInfo.topRight.x, cInfo.bottomLeft.y, 0));
00412 
00413                 if (PointIntersectRectangle(position, bl, tl, tr, br))
00414                     return i;
00415 
00416                 // Find the closest corner to position.
00417                 float dbl = DistanceToLine(bl, tl, position);
00418                 float dtl = DistanceToLine(tl, tr, position);
00419                 float dtr = DistanceToLine(tr, br, position);
00420                 float dbr = DistanceToLine(br, bl, position);
00421 
00422                 float d = dbl < dtl ? dbl : dtl;
00423                 d = d < dtr ? d : dtr;
00424                 d = d < dbr ? d : dbr;
00425 
00426                 if (distanceSqr > d)
00427                 {
00428                     distanceSqr = d;
00429                     closest = i;
00430                 }
00431             }
00432 
00433             return closest;
00434         }
