00551         // WORD HANDLING
00559         public static int FindIntersectingWord(TMP_Text text, Vector3 position, Camera camera)
00560         {
00561             RectTransform rectTransform = text.rectTransform;
00562 
00563             // Convert position into Worldspace coordinates
00564             ScreenPointToWorldPointInRectangle(rectTransform, position, camera, out position);
00565 
00566             for (int i = 0; i < text.textInfo.wordCount; i++)
00567             {
00568                 TMP_WordInfo wInfo = text.textInfo.wordInfo[i];
00569 
00570                 bool isBeginRegion = false;
00571 
00572                 Vector3 bl = Vector3.zero;
00573                 Vector3 tl = Vector3.zero;
00574                 Vector3 br = Vector3.zero;
00575                 Vector3 tr = Vector3.zero;
00576 
00577                 float maxAscender = -Mathf.Infinity;
00578                 float minDescender = Mathf.Infinity;
00579 
00580                 // Iterate through each character of the word
00581                 for (int j = 0; j < wInfo.characterCount; j++)
00582                 {
00583                     int characterIndex = wInfo.firstCharacterIndex + j;
00584                     TMP_CharacterInfo currentCharInfo = text.textInfo.characterInfo[characterIndex];
00585                     int currentLine = currentCharInfo.lineNumber;
00586 
00587                     bool isCharacterVisible = currentCharInfo.isVisible;
00588 
00589                     // Track maximum Ascender and minimum Descender for each word.
00590                     maxAscender = Mathf.Max(maxAscender, currentCharInfo.ascender);
00591                     minDescender = Mathf.Min(minDescender, currentCharInfo.descender);
00592 
00593                     if (isBeginRegion == false && isCharacterVisible)
00594                     {
00595                         isBeginRegion = true;
00596 
00597                         bl = new Vector3(currentCharInfo.bottomLeft.x, currentCharInfo.descender, 0);
00598                         tl = new Vector3(currentCharInfo.bottomLeft.x, currentCharInfo.ascender, 0);
00599 
00600                         //Debug.Log("Start Word Region at [" + currentCharInfo.character + "]");
00601 
00602                         // If Word is one character
00603                         if (wInfo.characterCount == 1)
00604                         {
00605                             isBeginRegion = false;
00606 
00607                             br = new Vector3(currentCharInfo.topRight.x, currentCharInfo.descender, 0);
00608                             tr = new Vector3(currentCharInfo.topRight.x, currentCharInfo.ascender, 0);
00609 
00610                             // Transform coordinates to be relative to transform and account min descender and max ascender.
00611                             bl = rectTransform.TransformPoint(new Vector3(bl.x, minDescender, 0));
00612                             tl = rectTransform.TransformPoint(new Vector3(tl.x, maxAscender, 0));
00613                             tr = rectTransform.TransformPoint(new Vector3(tr.x, maxAscender, 0));
00614                             br = rectTransform.TransformPoint(new Vector3(br.x, minDescender, 0));
00615 
00616                             // Check for Intersection
00617                             if (PointIntersectRectangle(position, bl, tl, tr, br))
00618                                 return i;
00619 
00620                             //Debug.Log("End Word Region at [" + currentCharInfo.character + "]");
00621                         }
00622                     }
00623 
00624                     // Last Character of Word
00625                     if (isBeginRegion && j == wInfo.characterCount - 1)
00626                     {
00627                         isBeginRegion = false;
00628 
00629                         br = new Vector3(currentCharInfo.topRight.x, currentCharInfo.descender, 0);
00630                         tr = new Vector3(currentCharInfo.topRight.x, currentCharInfo.ascender, 0);
00631 
00632                         // Transform coordinates to be relative to transform and account min descender and max ascender.
00633                         bl = rectTransform.TransformPoint(new Vector3(bl.x, minDescender, 0));
00634                         tl = rectTransform.TransformPoint(new Vector3(tl.x, maxAscender, 0));
00635                         tr = rectTransform.TransformPoint(new Vector3(tr.x, maxAscender, 0));
00636                         br = rectTransform.TransformPoint(new Vector3(br.x, minDescender, 0));
00637 
00638                         // Check for Intersection
00639                         if (PointIntersectRectangle(position, bl, tl, tr, br))
00640                             return i;
00641 
00642                         //Debug.Log("End Word Region at [" + currentCharInfo.character + "]");
00643                     }
00644                     // If Word is split on more than one line.
00645                     else if (isBeginRegion && currentLine != text.textInfo.characterInfo[characterIndex + 1].lineNumber)
00646                     {
00647                         isBeginRegion = false;
00648 
00649                         br = new Vector3(currentCharInfo.topRight.x, currentCharInfo.descender, 0);
00650                         tr = new Vector3(currentCharInfo.topRight.x, currentCharInfo.ascender, 0);
00651 
00652                         // Transform coordinates to be relative to transform and account min descender and max ascender.
00653                         bl = rectTransform.TransformPoint(new Vector3(bl.x, minDescender, 0));
00654                         tl = rectTransform.TransformPoint(new Vector3(tl.x, maxAscender, 0));
00655                         tr = rectTransform.TransformPoint(new Vector3(tr.x, maxAscender, 0));
00656                         br = rectTransform.TransformPoint(new Vector3(br.x, minDescender, 0));
00657 
00658                         maxAscender = -Mathf.Infinity;
00659                         minDescender = Mathf.Infinity;
00660 
00661                         // Check for Intersection
00662                         if (PointIntersectRectangle(position, bl, tl, tr, br))
00663                             return i;
00664 
00665                         //Debug.Log("End Word Region at [" + currentCharInfo.character + "]");
00666                     }
00667                 }
00668 
00669                 //Debug.Log("Word at Index: " + i + " is located at (" + bl + ", " + tl + ", " + tr + ", " + br + ").");
00670 
00671             }
00672 
00673             return -1;
00674         }
