00928 
00936         public static int FindNearestWord(TMP_Text text, Vector3 position, Camera camera)
00937         {
00938             RectTransform rectTransform = text.rectTransform;
00939 
00940             float distanceSqr = Mathf.Infinity;
00941             int closest = 0;
00942 
00943             // Convert position into Worldspace coordinates
00944             ScreenPointToWorldPointInRectangle(rectTransform, position, camera, out position);
00945 
00946             for (int i = 0; i < text.textInfo.wordCount; i++)
00947             {
00948                 TMP_WordInfo wInfo = text.textInfo.wordInfo[i];
00949 
00950                 bool isBeginRegion = false;
00951 
00952                 Vector3 bl = Vector3.zero;
00953                 Vector3 tl = Vector3.zero;
00954                 Vector3 br = Vector3.zero;
00955                 Vector3 tr = Vector3.zero;
00956 
00957                 // Iterate through each character of the word
00958                 for (int j = 0; j < wInfo.characterCount; j++)
00959                 {
00960                     int characterIndex = wInfo.firstCharacterIndex + j;
00961                     TMP_CharacterInfo currentCharInfo = text.textInfo.characterInfo[characterIndex];
00962                     int currentLine = currentCharInfo.lineNumber;
00963 
00964                     bool isCharacterVisible = currentCharInfo.isVisible;
00965 
00966                     if (isBeginRegion == false && isCharacterVisible)
00967                     {
00968                         isBeginRegion = true;
00969 
00970                         bl = rectTransform.TransformPoint(new Vector3(currentCharInfo.bottomLeft.x, currentCharInfo.descender, 0));
00971                         tl = rectTransform.TransformPoint(new Vector3(currentCharInfo.bottomLeft.x, currentCharInfo.ascender, 0));
00972 
00973                         //Debug.Log("Start Word Region at [" + currentCharInfo.character + "]");
00974 
00975                         // If Word is one character
00976                         if (wInfo.characterCount == 1)
00977                         {
00978                             isBeginRegion = false;
00979 
00980                             br = rectTransform.TransformPoint(new Vector3(currentCharInfo.topRight.x, currentCharInfo.descender, 0));
00981                             tr = rectTransform.TransformPoint(new Vector3(currentCharInfo.topRight.x, currentCharInfo.ascender, 0));
00982 
00983                             // Check for Intersection
00984                             if (PointIntersectRectangle(position, bl, tl, tr, br))
00985                                 return i;
00986 
00987                             // Find the closest line segment to position.
00988                             float dbl = DistanceToLine(bl, tl, position);
00989                             float dtl = DistanceToLine(tl, tr, position);
00990                             float dtr = DistanceToLine(tr, br, position);
00991                             float dbr = DistanceToLine(br, bl, position);
00992 
00993                             float d = dbl < dtl ? dbl : dtl;
00994                             d = d < dtr ? d : dtr;
00995                             d = d < dbr ? d : dbr;
00996 
00997                             if (distanceSqr > d)
00998                             {
00999                                 distanceSqr = d;
01000                                 closest = i;
01001                             }
01002                         }
01003                     }
01004 
01005                     // Last Character of Word
01006                     if (isBeginRegion && j == wInfo.characterCount - 1)
01007                     {
01008                         isBeginRegion = false;
01009 
01010                         br = rectTransform.TransformPoint(new Vector3(currentCharInfo.topRight.x, currentCharInfo.descender, 0));
01011                         tr = rectTransform.TransformPoint(new Vector3(currentCharInfo.topRight.x, currentCharInfo.ascender, 0));
01012 
01013                         // Check for Intersection
01014                         if (PointIntersectRectangle(position, bl, tl, tr, br))
01015                             return i;
01016 
01017                         // Find the closest line segment to position.
01018                         float dbl = DistanceToLine(bl, tl, position);
01019                         float dtl = DistanceToLine(tl, tr, position);
01020                         float dtr = DistanceToLine(tr, br, position);
01021                         float dbr = DistanceToLine(br, bl, position);
01022 
01023                         float d = dbl < dtl ? dbl : dtl;
01024                         d = d < dtr ? d : dtr;
01025                         d = d < dbr ? d : dbr;
01026 
01027                         if (distanceSqr > d)
01028                         {
01029                             distanceSqr = d;
01030                             closest = i;
01031                         }
01032                     }
01033                     // If Word is split on more than one line.
01034                     else if (isBeginRegion && currentLine != text.textInfo.characterInfo[characterIndex + 1].lineNumber)
01035                     {
01036                         isBeginRegion = false;
01037 
01038                         br = rectTransform.TransformPoint(new Vector3(currentCharInfo.topRight.x, currentCharInfo.descender, 0));
01039                         tr = rectTransform.TransformPoint(new Vector3(currentCharInfo.topRight.x, currentCharInfo.ascender, 0));
01040 
01041                         // Check for Intersection
01042                         if (PointIntersectRectangle(position, bl, tl, tr, br))
01043                             return i;
01044 
01045                         // Find the closest line segment to position.
01046                         float dbl = DistanceToLine(bl, tl, position);
01047                         float dtl = DistanceToLine(tl, tr, position);
01048                         float dtr = DistanceToLine(tr, br, position);
01049                         float dbr = DistanceToLine(br, bl, position);
01050 
01051                         float d = dbl < dtl ? dbl : dtl;
01052                         d = d < dtr ? d : dtr;
01053                         d = d < dbr ? d : dbr;
01054 
01055                         if (distanceSqr > d)
01056                         {
01057                             distanceSqr = d;
01058                             closest = i;
01059                         }
01060                     }
01061                 }
01062 
01063                 //Debug.Log("Word at Index: " + i + " is located at (" + bl + ", " + tl + ", " + tr + ", " + br + ").");
01064             }
01065 
01066             return closest;
01067         }
