01303 
01311         public static int FindIntersectingLine(TMP_Text text, Vector3 position, Camera camera)
01312         {
01313             RectTransform rectTransform = text.rectTransform;
01314 
01315             int closest = -1;
01316 
01317             // Convert position into Worldspace coordinates
01318             ScreenPointToWorldPointInRectangle(rectTransform, position, camera, out position);
01319 
01320             for (int i = 0; i < text.textInfo.lineCount; i++)
01321             {
01322                 TMP_LineInfo lineInfo = text.textInfo.lineInfo[i];
01323 
01324                 float ascender = rectTransform.TransformPoint(new Vector3(0, lineInfo.ascender, 0)).y;
01325                 float descender = rectTransform.TransformPoint(new Vector3(0, lineInfo.descender, 0)).y;
01326 
01327                 if (ascender > position.y && descender < position.y)
01328                 {
01329                     //Debug.Log("Position is on line " + i);
01330                     return i;
01331                 }
01332             }
01333 
01334             //Debug.Log("Closest line to position is " + closest);
01335             return closest;
01336         }
01337 
01338 
01346         public static int FindIntersectingLink(TMP_Text text, Vector3 position, Camera camera)
01347         {
01348             Transform rectTransform = text.transform;
01349 
01350             // Convert position into Worldspace coordinates
01351             ScreenPointToWorldPointInRectangle(rectTransform, position, camera, out position);
01352 
01353             for (int i = 0; i < text.textInfo.linkCount; i++)
01354             {
01355                 TMP_LinkInfo linkInfo = text.textInfo.linkInfo[i];
01356 
01357                 bool isBeginRegion = false;
01358 
01359                 Vector3 bl = Vector3.zero;
01360                 Vector3 tl = Vector3.zero;
01361                 Vector3 br = Vector3.zero;
01362                 Vector3 tr = Vector3.zero;
01363 
01364                 // Iterate through each character of the word
01365                 for (int j = 0; j < linkInfo.linkTextLength; j++)
01366                 {
01367                     int characterIndex = linkInfo.linkTextfirstCharacterIndex + j;
01368                     TMP_CharacterInfo currentCharInfo = text.textInfo.characterInfo[characterIndex];
01369                     int currentLine = currentCharInfo.lineNumber;
01370 
01371                     // Check if Link characters are on the current page
01372                     if (text.overflowMode == TextOverflowModes.Page && currentCharInfo.pageNumber + 1 != text.pageToDisplay) continue;
01373 
01374                     if (isBeginRegion == false)
01375                     {
01376                         isBeginRegion = true;
01377 
01378                         bl = rectTransform.TransformPoint(new Vector3(currentCharInfo.bottomLeft.x, currentCharInfo.descender, 0));
01379                         tl = rectTransform.TransformPoint(new Vector3(currentCharInfo.bottomLeft.x, currentCharInfo.ascender, 0));
01380 
01381                         //Debug.Log("Start Word Region at [" + currentCharInfo.character + "]");
01382 
01383                         // If Word is one character
01384                         if (linkInfo.linkTextLength == 1)
01385                         {
01386                             isBeginRegion = false;
01387 
01388                             br = rectTransform.TransformPoint(new Vector3(currentCharInfo.topRight.x, currentCharInfo.descender, 0));
01389                             tr = rectTransform.TransformPoint(new Vector3(currentCharInfo.topRight.x, currentCharInfo.ascender, 0));
01390 
01391                             // Check for Intersection
01392                             if (PointIntersectRectangle(position, bl, tl, tr, br))
01393                                 return i;
01394 
01395                             //Debug.Log("End Word Region at [" + currentCharInfo.character + "]");
01396                         }
01397                     }
01398 
01399                     // Last Character of Word
01400                     if (isBeginRegion && j == linkInfo.linkTextLength - 1)
01401                     {
01402                         isBeginRegion = false;
01403 
01404                         br = rectTransform.TransformPoint(new Vector3(currentCharInfo.topRight.x, currentCharInfo.descender, 0));
01405                         tr = rectTransform.TransformPoint(new Vector3(currentCharInfo.topRight.x, currentCharInfo.ascender, 0));
01406 
01407                         // Check for Intersection
01408                         if (PointIntersectRectangle(position, bl, tl, tr, br))
01409                             return i;
01410 
01411                         //Debug.Log("End Word Region at [" + currentCharInfo.character + "]");
01412                     }
01413                     // If Word is split on more than one line.
01414                     else if (isBeginRegion && currentLine != text.textInfo.characterInfo[characterIndex + 1].lineNumber)
01415                     {
01416                         isBeginRegion = false;
01417 
01418                         br = rectTransform.TransformPoint(new Vector3(currentCharInfo.topRight.x, currentCharInfo.descender, 0));
01419                         tr = rectTransform.TransformPoint(new Vector3(currentCharInfo.topRight.x, currentCharInfo.ascender, 0));
01420 
01421                         // Check for Intersection
01422                         if (PointIntersectRectangle(position, bl, tl, tr, br))
01423                             return i;
01424 
01425                         //Debug.Log("End Word Region at [" + currentCharInfo.character + "]");
01426                     }
01427                 }
01428 
01429                 //Debug.Log("Word at Index: " + i + " is located at (" + bl + ", " + tl + ", " + tr + ", " + br + ").");
01430 
01431             }
01432 
01433             return -1;
01434         }
