01631 
01639         public static int FindNearestLink(TMP_Text text, Vector3 position, Camera camera)
01640         {
01641             RectTransform rectTransform = text.rectTransform;
01642 
01643             // Convert position into Worldspace coordinates
01644             ScreenPointToWorldPointInRectangle(rectTransform, position, camera, out position);
01645 
01646             float distanceSqr = Mathf.Infinity;
01647             int closest = 0;
01648 
01649             for (int i = 0; i < text.textInfo.linkCount; i++)
01650             {
01651                 TMP_LinkInfo linkInfo = text.textInfo.linkInfo[i];
01652 
01653                 bool isBeginRegion = false;
01654 
01655                 Vector3 bl = Vector3.zero;
01656                 Vector3 tl = Vector3.zero;
01657                 Vector3 br = Vector3.zero;
01658                 Vector3 tr = Vector3.zero;
01659 
01660                 // Iterate through each character of the link
01661                 for (int j = 0; j < linkInfo.linkTextLength; j++)
01662                 {
01663                     int characterIndex = linkInfo.linkTextfirstCharacterIndex + j;
01664                     TMP_CharacterInfo currentCharInfo = text.textInfo.characterInfo[characterIndex];
01665                     int currentLine = currentCharInfo.lineNumber;
01666 
01667                     // Check if Link characters are on the current page
01668                     if (text.overflowMode == TextOverflowModes.Page && currentCharInfo.pageNumber + 1 != text.pageToDisplay) continue;
01669 
01670                     if (isBeginRegion == false)
01671                     {
01672                         isBeginRegion = true;
01673 
01674                         //Debug.Log("Start Word Region at [" + currentCharInfo.character + "]");
01675 
01676                         bl = rectTransform.TransformPoint(new Vector3(currentCharInfo.bottomLeft.x, currentCharInfo.descender, 0));
01677                         tl = rectTransform.TransformPoint(new Vector3(currentCharInfo.bottomLeft.x, currentCharInfo.ascender, 0));
01678 
01679                         // If Link is one character
01680                         if (linkInfo.linkTextLength == 1)
01681                         {
01682                             isBeginRegion = false;
01683 
01684                             br = rectTransform.TransformPoint(new Vector3(currentCharInfo.topRight.x, currentCharInfo.descender, 0));
01685                             tr = rectTransform.TransformPoint(new Vector3(currentCharInfo.topRight.x, currentCharInfo.ascender, 0));
01686 
01687                             // Check for Intersection
01688                             if (PointIntersectRectangle(position, bl, tl, tr, br))
01689                                 return i;
01690 
01691                             // Find the closest line segment to position.
01692                             float dbl = DistanceToLine(bl, tl, position); 
01693                             float dtl = DistanceToLine(tl, tr, position); 
01694                             float dtr = DistanceToLine(tr, br, position); 
01695                             float dbr = DistanceToLine(br, bl, position);
01696 
01697                             float d = dbl < dtl ? dbl : dtl;
01698                             d = d < dtr ? d : dtr;
01699                             d = d < dbr ? d : dbr;
01700 
01701                             if (distanceSqr > d)
01702                             {
01703                                 distanceSqr = d;
01704                                 closest = i;
01705                             }
01706 
01707                         }
01708                     }
01709 
01710                     // Last Character of Word
01711                     if (isBeginRegion && j == linkInfo.linkTextLength - 1)
01712                     {
01713                         isBeginRegion = false;
01714 
01715                         br = rectTransform.TransformPoint(new Vector3(currentCharInfo.topRight.x, currentCharInfo.descender, 0));
01716                         tr = rectTransform.TransformPoint(new Vector3(currentCharInfo.topRight.x, currentCharInfo.ascender, 0));
01717 
01718                         // Check for Intersection
01719                         if (PointIntersectRectangle(position, bl, tl, tr, br))
01720                             return i;
01721 
01722                         // Find the closest line segment to position.
01723                         float dbl = DistanceToLine(bl, tl, position);
01724                         float dtl = DistanceToLine(tl, tr, position);
01725                         float dtr = DistanceToLine(tr, br, position);
01726                         float dbr = DistanceToLine(br, bl, position);
01727 
01728                         float d = dbl < dtl ? dbl : dtl;
01729                         d = d < dtr ? d : dtr;
01730                         d = d < dbr ? d : dbr;
01731 
01732                         if (distanceSqr > d)
01733                         {
01734                             distanceSqr = d;
01735                             closest = i;
01736                         }
01737 
01738                     }
01739                     // If Link is split on more than one line.
01740                     else if (isBeginRegion && currentLine != text.textInfo.characterInfo[characterIndex + 1].lineNumber)
01741                     {
01742                         isBeginRegion = false;
01743 
01744                         br = rectTransform.TransformPoint(new Vector3(currentCharInfo.topRight.x, currentCharInfo.descender, 0));
01745                         tr = rectTransform.TransformPoint(new Vector3(currentCharInfo.topRight.x, currentCharInfo.ascender, 0));
01746 
01747                         // Check for Intersection
01748                         if (PointIntersectRectangle(position, bl, tl, tr, br))
01749                             return i;
01750 
01751                         // Find the closest line segment to position.
01752                         float dbl = DistanceToLine(bl, tl, position);
01753                         float dtl = DistanceToLine(tl, tr, position);
01754                         float dtr = DistanceToLine(tr, br, position);
01755                         float dbr = DistanceToLine(br, bl, position);
01756 
01757                         float d = dbl < dtl ? dbl : dtl;
01758                         d = d < dtr ? d : dtr;
01759                         d = d < dbr ? d : dbr;
01760 
01761                         if (distanceSqr > d)
01762                         {
01763                             distanceSqr = d;
01764                             closest = i;
01765                         }
01766                     }
01767                 }
01768 
01769                 //Debug.Log("Word at Index: " + i + " is located at (" + bl + ", " + tl + ", " + tr + ", " + br + ").");
01770 
01771             }
01772 
01773             return closest;
01774         }
