02012         private static bool PointIntersectRectangle(Vector3 m, Vector3 a, Vector3 b, Vector3 c, Vector3 d)
02013         {
02014             Vector3 ab = b - a;
02015             Vector3 am = m - a;
02016             Vector3 bc = c - b;
02017             Vector3 bm = m - b;
02018 
02019             float abamDot = Vector3.Dot(ab, am);
02020             float bcbmDot = Vector3.Dot(bc, bm);
02021 
02022             return 0 <= abamDot && abamDot <= Vector3.Dot(ab, ab) && 0 <= bcbmDot && bcbmDot <= Vector3.Dot(bc, bc);
02023         }
02024 
02025 
02034         public static bool ScreenPointToWorldPointInRectangle(Transform transform, Vector2 screenPoint, Camera cam, out Vector3 worldPoint)
02035         {
02036             worldPoint = (Vector3)Vector2.zero;
02037             Ray ray = RectTransformUtility.ScreenPointToRay(cam, screenPoint);
02038             float enter;
02039             if (!new Plane(transform.rotation * Vector3.back, transform.position).Raycast(ray, out enter))
02040                 return false;
02041             worldPoint = ray.GetPoint(enter);
02042             return true;
02043         }
