02046         private struct LineSegment
02047         {
02048             public Vector3 Point1;
02049             public Vector3 Point2;
02050 
02051             public LineSegment(Vector3 p1, Vector3 p2)
02052             {
02053                 Point1 = p1;
02054                 Point2 = p2;
02055             }
02056         }
02057 
02058 
02067         private static bool IntersectLinePlane(LineSegment line, Vector3 point, Vector3 normal, out Vector3 intersectingPoint)
02068         {
02069             intersectingPoint = Vector3.zero;
02070             Vector3 u = line.Point2 - line.Point1;
02071             Vector3 w = line.Point1 - point;
02072 
02073             float D = Vector3.Dot(normal, u);
02074             float N = -Vector3.Dot(normal, w);
02075 
02076             if (Mathf.Abs(D) < Mathf.Epsilon)   // if line is parallel & co-planar to plane
02077             {
02078                 if (N == 0)
02079                     return true;
02080                 else
02081                     return false;
02082             }
02083 
02084             float sI = N / D;
02085 
02086             if (sI < 0 || sI > 1) // Line parallel to plane
02087                 return false;
02088 
02089             intersectingPoint = line.Point1 + sI * u;
02090 
02091             return true;
02092         }
02093 
02094 
02102         public static float DistanceToLine(Vector3 a, Vector3 b, Vector3 point)
02103         {            
02104             Vector3 n = b - a;
02105             Vector3 pa = a - point;
02106  
02107             float c = Vector3.Dot( n, pa );
02108  
02109             // Closest point is a
02110             if ( c > 0.0f )
02111                 return Vector3.Dot( pa, pa );
02112  
02113             Vector3 bp = point - b;
02114  
02115             // Closest point is b
02116             if (Vector3.Dot( n, bp ) > 0.0f )
02117                 return Vector3.Dot( bp, bp );
02118  
02119             // Closest point is between a and b
02120             Vector3 e = pa - n * (c / Vector3.Dot( n, n ));
02121  
02122             return Vector3.Dot( e, e );
02123         }
