02164         const string k_lookupStringL = "-------------------------------- !-#$%&-()*+,-./0123456789:;<=>?@abcdefghijklmnopqrstuvwxyz[-]^_`abcdefghijklmnopqrstuvwxyz{|}~-";
02165 
02169         const string k_lookupStringU = "-------------------------------- !-#$%&-()*+,-./0123456789:;<=>?@ABCDEFGHIJKLMNOPQRSTUVWXYZ[-]^_`ABCDEFGHIJKLMNOPQRSTUVWXYZ{|}~-";
02170 
02171 
02175         public static char ToLowerFast(char c)
02176         {
02177             if (c > k_lookupStringL.Length - 1)
02178                 return c;
02179 
02180             return k_lookupStringL[c];
02181         }
02182 
02186         public static char ToUpperFast(char c)
02187         {
02188             if (c > k_lookupStringU.Length - 1)
02189                 return c;
02190 
02191             return k_lookupStringU[c];
02192         }
02193 
02198         public static int GetSimpleHashCode(string s)
02199         {
02200             int hashCode = 0; // 5381;
02201 
02202             for (int i = 0; i < s.Length; i++)
02203                 hashCode = (hashCode << 5) + hashCode ^ s[i];
02204 
02205             return hashCode;
02206         }
02207 
02212         public static uint GetSimpleHashCodeLowercase(string s)
02213         {
02214             uint hashCode = 5381;
02215 
02216             for (int i = 0; i < s.Length; i++)
02217                 hashCode = (hashCode << 5) + hashCode ^ ToLowerFast(s[i]);
02218 
02219             return hashCode;
02220         }
