02228         public static int HexToInt(char hex)
02229         {
02230             switch (hex)
02231             {
02232                 case '0': return 0;
02233                 case '1': return 1;
02234                 case '2': return 2;
02235                 case '3': return 3;
02236                 case '4': return 4;
02237                 case '5': return 5;
02238                 case '6': return 6;
02239                 case '7': return 7;
02240                 case '8': return 8;
02241                 case '9': return 9;
02242                 case 'A': return 10;
02243                 case 'B': return 11;
02244                 case 'C': return 12;
02245                 case 'D': return 13;
02246                 case 'E': return 14;
02247                 case 'F': return 15;
02248                 case 'a': return 10;
02249                 case 'b': return 11;
02250                 case 'c': return 12;
02251                 case 'd': return 13;
02252                 case 'e': return 14;
02253                 case 'f': return 15;
02254             }
02255             return 15;
02256         }
02257 
02258 
02264         public static int StringToInt(string s)
02265         {
02266             int value = 0;
02267 
02268             for (int i = 0; i < s.Length; i++)
02269             {
02270                 value += HexToInt(s[i]) * (int)Mathf.Pow(16, (s.Length - 1) - i);
02271             }
02272 
02273             return value;
02274         }
