00054         public static void RegisterCanvasElementForLayoutRebuild(ICanvasElement element)
00055         {
00056             TMP_UpdateRegistry.instance.InternalRegisterCanvasElementForLayoutRebuild(element);
00057         }
00058 
00059         private bool InternalRegisterCanvasElementForLayoutRebuild(ICanvasElement element)
00060         {
00061             int id = (element as Object).GetInstanceID();
00062 
00063             if (this.m_LayoutQueueLookup.ContainsKey(id))
00064                 return false;
00065 
00066             m_LayoutQueueLookup[id] = id;
00067             this.m_LayoutRebuildQueue.Add(element);
00068 
00069             return true;
00070         }
00071 
00072 
00077         public static void RegisterCanvasElementForGraphicRebuild(ICanvasElement element)
