00080         }
00081 
00082         private bool InternalRegisterCanvasElementForGraphicRebuild(ICanvasElement element)
00083         {
00084             int id = (element as Object).GetInstanceID();
00085 
00086             if (this.m_GraphicQueueLookup.ContainsKey(id))
00087                 return false;
00088 
00089             m_GraphicQueueLookup[id] = id;
00090             this.m_GraphicRebuildQueue.Add(element);
00091 
00092             return true;
00093         }
00094 
00095 
00099         private void PerformUpdateForCanvasRendererObjects()
00100         {
00101             //Debug.Log("Performing update of CanvasRenderer objects at Frame: " + Time.frameCount);
00102 
00103             // Processing elements that require a layout rebuild.
00104             //this.m_PerformingLayoutUpdate = true;
00105             for (int index = 0; index < m_LayoutRebuildQueue.Count; index++)
00106             {
00107                 ICanvasElement element = TMP_UpdateRegistry.instance.m_LayoutRebuildQueue[index];
00108 
00109                 element.Rebuild(CanvasUpdate.Prelayout);
00110             }
00111             //this.m_PerformingLayoutUpdate = false;
00112             if (m_LayoutRebuildQueue.Count > 0)
00113             {
00114                 m_LayoutRebuildQueue.Clear();
00115                 m_LayoutQueueLookup.Clear();
00116             }
00117 
00118 
00119             // Processing elements that require a graphic rebuild.
00120             //this.m_PerformingGraphicUpdate = true;
00121             for (int index = 0; index < m_GraphicRebuildQueue.Count; index++)
00122             {
00123                 ICanvasElement element = TMP_UpdateRegistry.instance.m_GraphicRebuildQueue[index];
00124 
00125                 element.Rebuild(CanvasUpdate.PreRender);
00126             }
00127             //this.m_PerformingGraphicUpdate = false;
00128 
00129             // If there are no objects in the queue, we don't need to clear the lists again.
00130             if (m_GraphicRebuildQueue.Count > 0)
00131             {
00132                 m_GraphicRebuildQueue.Clear();
00133                 m_GraphicQueueLookup.Clear();
00134             }
00135         }
00136 
