00026         public static void ShowFontAtlasCreatorWindow(Font sourceFontFile)
00027         {
00028             var window = GetWindow<TMPro_FontAssetCreatorWindow>();
00029 
00030             window.titleContent = new GUIContent("Font Asset Creator");
00031             window.Focus();
00032 
00033             // Override selected font asset
00034             window.ClearGeneratedData();
00035             window.m_SelectedFontAsset = null;
00036             window.m_LegacyFontAsset = null;
00037             window.m_SourceFontFile = sourceFontFile;
00038 
00039             // Make sure TMP Essential Resources have been imported.
00040             window.CheckEssentialResources();
00041         }
