00043 
00044         public static void ShowFontAtlasCreatorWindow(TMP_FontAsset fontAsset)
00045         {
00046             var window = GetWindow<TMPro_FontAssetCreatorWindow>();
00047 
00048             window.titleContent = new GUIContent("Font Creator");
00049             window.Focus();
00050 
00051             // Clear any previously generated data
00052             window.ClearGeneratedData();
00053             window.m_LegacyFontAsset = null;
00054 
00055             // Load font asset creation settings if we have valid settings
00056             if (!string.IsNullOrEmpty(fontAsset.creationSettings.sourceFontFileGUID))
00057             {
00058                 window.LoadFontCreationSettings(fontAsset.creationSettings);
00059                 window.m_SavedFontAtlas = fontAsset.atlas;
00060             }
00061             else
00062             {
00063                 window.m_WarningMessage = "Font Asset [" + fontAsset.name + "] does not contain any previous \"Font Asset Creation Settings\". This usually means [" + fontAsset.name + "] was created before this new functionality was added.";
00064                 window.m_SourceFontFile = null;
00065                 window.m_LegacyFontAsset = fontAsset;
00066             }
00067 
00068             // Even if we don't have any saved generation settings, we still want to pre-select the source font file.
00069             window.m_SelectedFontAsset = fontAsset;
00070 
00071             // Make sure TMP Essential Resources have been imported.
00072             window.CheckEssentialResources();
00073         }
