00148         public void OnEnable()
00149         {
00150             minSize = new Vector2(315, minSize.y);
00151 
00152             // Used for Diagnostics
00153             m_StopWatch = new System.Diagnostics.Stopwatch();
00154 
00155             // Initialize & Get shader property IDs.
00156             ShaderUtilities.GetShaderPropertyIDs();
00157 
00158             // Load last selected preset if we are not already in the process of regenerating an existing font asset (via the Context menu)
00159             if (EditorPrefs.HasKey(k_FontAssetCreationSettingsContainerKey))
00160             {
00161                 if (m_FontAssetCreationSettingsContainer == null)
00162                     m_FontAssetCreationSettingsContainer = JsonUtility.FromJson<FontAssetCreationSettingsContainer>(EditorPrefs.GetString(k_FontAssetCreationSettingsContainerKey));
00163 
00164                 if (m_FontAssetCreationSettingsContainer.fontAssetCreationSettings != null && m_FontAssetCreationSettingsContainer.fontAssetCreationSettings.Count > 0)
00165                 {
00166                     // Load Font Asset Creation Settings preset.
00167                     if (EditorPrefs.HasKey(k_FontAssetCreationSettingsCurrentIndexKey))
00168                         m_FontAssetCreationSettingsCurrentIndex = EditorPrefs.GetInt(k_FontAssetCreationSettingsCurrentIndexKey);
00169 
00170                     LoadFontCreationSettings(m_FontAssetCreationSettingsContainer.fontAssetCreationSettings[m_FontAssetCreationSettingsCurrentIndex]);
00171                 }
00172             }
00173 
00174             // Debug Link to received message from Native Code
00175             //TMPro_FontPlugin.LinkDebugLog(); // Link with C++ Plugin to get Debug output
00176         }
