00178         public void OnDisable()
00179         {
00180             //Debug.Log("TextMeshPro Editor Window has been disabled.");
00181 
00182             // Cancel font asset generation just in case one is in progress.
00183             TMPro_FontPlugin.SendCancellationRequest(CancellationRequestType.WindowClosed);
00184 
00185             // Destroy Engine only if it has been initialized already
00186             TMPro_FontPlugin.Destroy_FontEngine();
00187             
00188             if (m_FontAtlas != null && EditorUtility.IsPersistent(m_FontAtlas) == false)
00189             {
00190                 //Debug.Log("Destroying font_Atlas!");
00191                 DestroyImmediate(m_FontAtlas);
00192             }
00193             
00194             if (File.Exists("Assets/TextMesh Pro/Glyph Report.txt"))
00195             {
00196                 File.Delete("Assets/TextMesh Pro/Glyph Report.txt");
00197                 File.Delete("Assets/TextMesh Pro/Glyph Report.txt.meta");
00198 
00199                 AssetDatabase.Refresh();
00200             }
00201 
00202             // Save Font Asset Creation Settings Index
00203             SaveCreationSettingsToEditorPrefs(SaveFontCreationSettings());
00204             EditorPrefs.SetInt(k_FontAssetCreationSettingsCurrentIndexKey, m_FontAssetCreationSettingsCurrentIndex);
00205 
00206             // Unregister to event
00207             TMPro_EventManager.RESOURCE_LOAD_EVENT.Remove(ON_RESOURCES_LOADED);
00208 
00209             Resources.UnloadUnusedAssets();
00210         }
