00225             {
00226                 if (m_IsGenerationDisabled == false)
00227                     TMPro_EventManager.RESOURCE_LOAD_EVENT.Add(ON_RESOURCES_LOADED);
00228 
00229                 m_IsGenerationDisabled = true;
00230             }
00231         }
