00246         public void Update()
00247         {
00248             if (m_IsRepaintNeeded)
00249             {
00250                 //Debug.Log("Repainting...");
00251                 m_IsRepaintNeeded = false;
00252                 Repaint();
00253             }
00254 
00255             // Update Progress bar is we are Rendering a Font.
00256             if (m_IsProcessing)
00257             {
00258                 m_RenderingProgress = TMPro_FontPlugin.Check_RenderProgress();
00259 
00260                 m_IsRepaintNeeded = true;
00261             }
00262 
00263             // Update Feedback Window & Create Font Texture once Rendering is done.
00264             if (m_IsRenderingDone)
00265             {
00266                 // Stop StopWatch
00267                 m_StopWatch.Stop();
00268                 Debug.Log("Font Atlas generation completed in: " + m_StopWatch.Elapsed.TotalMilliseconds.ToString("0.000 ms."));
00269                 m_StopWatch.Reset();
00270 
00271                 m_IsProcessing = false;
00272                 m_IsRenderingDone = false;
00273 
00274                 if (m_IsGenerationCancelled == false)
00275                 {
00276                     UpdateRenderFeedbackWindow();
00277                     CreateFontTexture();
00278                 }
00279                 Repaint();
00280             }
00281         }
