00289         static int[] ParseNumberSequence(string sequence)
00290         {
00291             List<int> unicodeList = new List<int>();
00292             string[] sequences = sequence.Split(',');
00293 
00294             foreach (string seq in sequences)
00295             {
00296                 string[] s1 = seq.Split('-');
00297 
00298                 if (s1.Length == 1)
00299                     try
00300                     {
00301                         unicodeList.Add(int.Parse(s1[0]));
00302                     }
00303                     catch
00304                     {
00305                         Debug.Log("No characters selected or invalid format.");
00306                     }
00307                 else
00308                 {
00309                     for (int j = int.Parse(s1[0]); j < int.Parse(s1[1]) + 1; j++)
00310                     {
00311                         unicodeList.Add(j);
00312                     }
00313                 }
00314             }
00315 
00316             return unicodeList.ToArray();
00317         }
00318 
00319 
00325         static int[] ParseHexNumberSequence(string sequence)
00326         {
00327             List<int> unicodeList = new List<int>();
00328             string[] sequences = sequence.Split(',');
00329 
00330             foreach (string seq in sequences)
00331             {
00332                 string[] s1 = seq.Split('-');
00333 
00334                 if (s1.Length == 1)
00335                     try
00336                     {
00337                         unicodeList.Add(int.Parse(s1[0], NumberStyles.AllowHexSpecifier));
00338                     }
00339                     catch
00340                     {
00341                         Debug.Log("No characters selected or invalid format.");
00342                     }
00343                 else
00344                 {
00345                     for (int j = int.Parse(s1[0], NumberStyles.AllowHexSpecifier); j < int.Parse(s1[1], NumberStyles.AllowHexSpecifier) + 1; j++)
00346                     {
00347                         unicodeList.Add(j);
00348                     }
00349                 }
00350             }
00351 
00352             return unicodeList.ToArray();
00353         }
