00627                         else
00628                             Debug.Log("Error Code: " + errorCode + "  occurred while Initializing the FreeType Library.");
00629                     }
00630                     
00631                     string fontPath = AssetDatabase.GetAssetPath(m_SourceFontFile); // Get file path of TTF Font.
00632 
00633                     if (errorCode == 0)
00634                     {
00635                         errorCode = TMPro_FontPlugin.Load_TrueType_Font(fontPath); // Load the selected font.
00636 
00637                         if (errorCode != 0)
00638                         {
00639                             if (errorCode == 0xF1)
00640                             {
00641                                 //Debug.Log("Font was already loaded!");
00642                                 errorCode = 0;
00643                             }
00644                             else
00645                                 Debug.Log("Error Code: " + errorCode + "  occurred while Loading the [" + m_SourceFontFile.name + "] font file. This typically results from the use of an incompatible or corrupted font file.");
00646                         }
00647                     }
00648 
00649                     if (errorCode == 0)
00650                     {
00651                         if (m_PointSizeSamplingMode == 0) m_PointSize = 72; // If Auto set size to 72 pts.
00652 
00653                         errorCode = TMPro_FontPlugin.FT_Size_Font(m_PointSize); // Load the selected font and size it accordingly.
00654                         if (errorCode != 0)
00655                             Debug.Log("Error Code: " + errorCode + "  occurred while Sizing the font.");
00656                     }
00657 
00658                     // Define an array containing the characters we will render.
00659                     if (errorCode == 0)
00660                     {
00661                         int[] characterSet;
00662                         if (m_CharacterSetSelectionMode == 7 || m_CharacterSetSelectionMode == 8)
00663                         {
00664                             List<int> charList = new List<int>();
00665 
00666                             for (int i = 0; i < m_CharacterSequence.Length; i++)
00667                             {
00668                                 // Check to make sure we don't include duplicates
00669                                 if (charList.FindIndex(item => item == m_CharacterSequence[i]) == -1)
00670                                     charList.Add(m_CharacterSequence[i]);
00671                                 else
00672                                 {
00673                                     //Debug.Log("Character [" + characterSequence[i] + "] is a duplicate.");
00674                                 }
00675                             }
00676 
00677                             characterSet = charList.ToArray();
00678                         }
00679                         else if (m_CharacterSetSelectionMode == 6)
00680                         {
00681                             characterSet = ParseHexNumberSequence(m_CharacterSequence);
00682                         }
00683                         else
00684                         {
00685                             characterSet = ParseNumberSequence(m_CharacterSequence);
00686                         }
00687 
00688                         m_CharacterCount = characterSet.Length;
00689                         
00690                         m_TextureBuffer = new byte[m_AtlasWidth * m_AtlasHeight];
00691 
00692                         m_FontFaceInfo = new FT_FaceInfo();
00693 
00694                         m_FontGlyphInfo = new FT_GlyphInfo[m_CharacterCount];
00695                         
00696                         int padding = m_Padding;
00697 
00698                         bool autoSizing = m_PointSizeSamplingMode == 0;
00699 
00700                         float strokeSize = m_FontStyleValue;
00701                         if (m_RenderMode == RenderModes.DistanceField16) strokeSize = m_FontStyleValue * 16;
00702                         if (m_RenderMode == RenderModes.DistanceField32) strokeSize = m_FontStyleValue * 32;
00703 
00704                         m_IsProcessing = true;
00705                         m_IsGenerationCancelled = false;
00706 
00707                         // Start Stop Watch
00708                         m_StopWatch = System.Diagnostics.Stopwatch.StartNew();
00709 
00710                         ThreadPool.QueueUserWorkItem(someTask =>
00711                         {
00712                             m_IsRenderingDone = false;
00713 
00714                             errorCode = TMPro_FontPlugin.Render_Characters(m_TextureBuffer, m_AtlasWidth, m_AtlasHeight, padding, characterSet, m_CharacterCount, m_FontStyle, strokeSize, autoSizing, m_RenderMode, (int)m_PackingMode, ref m_FontFaceInfo, m_FontGlyphInfo);
00715                             m_IsRenderingDone = true;
00716                         });
00717 
00718                     }
00719 
00720                     SaveCreationSettingsToEditorPrefs(SaveFontCreationSettings());
00721                 }
00722             }
00723 
00724             // FONT RENDERING PROGRESS BAR
00725             GUILayout.Space(1);
00726 
00727             Rect progressRect = EditorGUILayout.GetControlRect(false, 20);
00728 
00729             bool isEnabled = GUI.enabled;
00730             GUI.enabled = true;
00731             EditorGUI.ProgressBar(progressRect, m_IsProcessing ? m_RenderingProgress : 0, "Generation Progress");
00732             progressRect.x = progressRect.x + progressRect.width - 20;
00733             progressRect.y += 1;
00734             progressRect.width = 20;
00735             progressRect.height = 16;
00736 
00737             GUI.enabled = m_IsProcessing;
00738             if (GUI.Button(progressRect, "X"))
00739             {
00740                 TMPro_FontPlugin.SendCancellationRequest(CancellationRequestType.CancelInProgess);
00741                 m_RenderingProgress = 0;
00742                 m_IsProcessing = false;
00743                 m_IsGenerationCancelled = true;
00744             }
00745             GUI.enabled = isEnabled;
00746             
00747             // FONT STATUS & INFORMATION
00748             GUISkin skin = GUI.skin;
00749 
00750             //GUI.skin = TMP_UIStyleManager.TMP_GUISkin;
00751             GUI.enabled = true;
00752             
00753             GUILayout.BeginVertical(EditorStyles.helpBox, GUILayout.Height(145));
00754             m_OutputScrollPosition = EditorGUILayout.BeginScrollView(m_OutputScrollPosition);
00755             EditorGUILayout.LabelField(m_OutputFeedback, TMP_UIStyleManager.label);
00756             EditorGUILayout.EndScrollView();
00757             GUILayout.EndVertical();
00758 
00759             GUI.skin = skin;
00760             
00761             // SAVE TEXTURE & CREATE and SAVE FONT XML FILE
00762             GUI.enabled = m_FontAtlas != null && !m_IsProcessing;    // Enable Save Button if font_Atlas is not Null.
00763             
00764             EditorGUILayout.BeginHorizontal();
00765                 
00766             if (GUILayout.Button("Save") && GUI.enabled)
00767             {
00768                 if (m_SelectedFontAsset == null)
00769                 {
00770                     if (m_LegacyFontAsset != null)
00771                         SaveNewFontAssetWithSameName(m_LegacyFontAsset);
00772                     else
00773                         SaveNewFontAsset(m_SourceFontFile);
00774                 }
00775                 else
00776                 {
00777                     // Save over exiting Font Asset
00778                     string filePath = Path.GetFullPath(AssetDatabase.GetAssetPath(m_SelectedFontAsset)).Replace('\\', '/');
00779 
00780                     if (m_RenderMode < RenderModes.DistanceField16) // ((int)m_RenderMode & 0x10) == 0x10)
00781                         Save_Normal_FontAsset(filePath);
00782                     else // ((RasterModes)m_RenderMode & RasterModes.Raster_Mode_SDF) == RasterModes.Raster_Mode_SDF || m_RenderMode == RenderModes.DistanceFieldAA)
00783                         Save_SDF_FontAsset(filePath);
00784                 }
00785             }
00786             if (GUILayout.Button("Save as...") && GUI.enabled)
00787             {
00788                 if (m_SelectedFontAsset == null)
00789                 {
00790                     SaveNewFontAsset(m_SourceFontFile);
00791                 }
00792                 else
00793                 {
00794                     SaveNewFontAssetWithSameName(m_SelectedFontAsset);
00795                 }
00796             }
00797                 
00798             EditorGUILayout.EndHorizontal();
00799 
00800             EditorGUILayout.Space();
00801             
00802             GUI.enabled = true; // Re-enable GUI
00803 
00804             GUILayout.Space(5);
00805             
00806             if (position.height > position.width || position.width < k_TwoColumnControlsWidth)
00807             {
00808                 DrawPreview();
00809                 GUILayout.Space(5);
00810             }
00811             
00812             EditorGUILayout.EndScrollView();
00813 
00814             if (m_IsFontAtlasInvalid)
00815                 ClearGeneratedData();
00816         }
