00822         void ClearGeneratedData()
00823         {
00824             m_IsFontAtlasInvalid = false;
00825 
00826             if (m_FontAtlas != null)
00827             {
00828                 DestroyImmediate(m_FontAtlas);
00829                 m_FontAtlas = null;
00830             }
00831         
00832             m_SavedFontAtlas = null;
00833 
00834             m_OutputFeedback = string.Empty;
00835             m_WarningMessage = string.Empty;
00836         }
