00842         void UpdateRenderFeedbackWindow()
00843         {
00844             m_PointSize = m_FontFaceInfo.pointSize;
00845 
00846             string colorTag = m_FontFaceInfo.characterCount == m_CharacterCount ? "<color=#C0ffff>" : "<color=#ffff00>";
00847             string colorTag2 = "<color=#C0ffff>";
00848 
00849             var missingGlyphReport = k_OutputNameLabel + "<b>" + colorTag2 + m_FontFaceInfo.name + "</color></b>";
00850 
00851             if (missingGlyphReport.Length > 60)
00852                 missingGlyphReport += "\n" + k_OutputSizeLabel + "<b>" + colorTag2 + m_FontFaceInfo.pointSize + "</color></b>";
00853             else
00854                 missingGlyphReport += "  " + k_OutputSizeLabel + "<b>" + colorTag2 + m_FontFaceInfo.pointSize + "</color></b>";
00855 
00856             missingGlyphReport += "\n" + k_OutputCountLabel + "<b>" + colorTag + m_FontFaceInfo.characterCount + "/" + m_CharacterCount + "</color></b>";
00857 
00858             // Report missing requested glyph
00859             missingGlyphReport += "\n\n<color=#ffff00><b>Missing Characters</b></color>";
00860             missingGlyphReport += "\n----------------------------------------";
00861             
00862             m_OutputFeedback = missingGlyphReport;
00863 
00864             for (int i = 0; i < m_CharacterCount; i++)
00865             {
00866                 if (m_FontGlyphInfo[i].x == -1)
00867                 {
00868                     missingGlyphReport += "\nID: <color=#C0ffff>" + m_FontGlyphInfo[i].id + "\t</color>Hex: <color=#C0ffff>" + m_FontGlyphInfo[i].id.ToString("X") + "\t</color>Char [<color=#C0ffff>" + (char)m_FontGlyphInfo[i].id + "</color>]";
00869 
00870                     if (missingGlyphReport.Length < 16300)
00871                         m_OutputFeedback = missingGlyphReport;
00872                 }
00873             }
00874 
00875             if (missingGlyphReport.Length > 16300)
00876                 m_OutputFeedback += "\n\n<color=#ffff00>Report truncated.</color>\n<color=#c0ffff>See</color> \"TextMesh Pro\\Glyph Report.txt\"";
00877 
00878             // Save Missing Glyph Report file
00879             if (Directory.Exists("Assets/TextMesh Pro"))
00880             {
00881                 missingGlyphReport = System.Text.RegularExpressions.Regex.Replace(missingGlyphReport, @"<[^>]*>", string.Empty);
00882                 File.WriteAllText("Assets/TextMesh Pro/Glyph Report.txt", missingGlyphReport);
00883                 AssetDatabase.Refresh();
00884             }
00885         }
