00887 
00888         void CreateFontTexture()
00889         {
00890             m_FontAtlas = new Texture2D(m_AtlasWidth, m_AtlasHeight, TextureFormat.Alpha8, false, true);
00891 
00892             Color32[] colors = new Color32[m_AtlasWidth * m_AtlasHeight];
00893 
00894             for (int i = 0; i < (m_AtlasWidth * m_AtlasHeight); i++)
00895             {
00896                 byte c = m_TextureBuffer[i];
00897                 colors[i] = new Color32(c, c, c, c);
00898             }
00899             // Clear allocation of 
00900             m_TextureBuffer = null;
00901             
00902             if (m_RenderMode == RenderModes.Raster || m_RenderMode == RenderModes.RasterHinted)
00903                 m_FontAtlas.filterMode = FilterMode.Point;
00904 
00905             m_FontAtlas.SetPixels32(colors, 0);
00906             m_FontAtlas.Apply(false, true);
00907         }
