00909 
00914         void SaveNewFontAsset(Object sourceObject)
00915         {
00916             string filePath;
00917             
00918             // Save new Font Asset and open save file requester at Source Font File location.
00919             string saveDirectory = new FileInfo(AssetDatabase.GetAssetPath(sourceObject)).DirectoryName;
00920 
00921             if (m_RenderMode < RenderModes.DistanceField16) // ((int)m_RenderMode & 0x10) == 0x10)
00922             {
00923                 filePath = EditorUtility.SaveFilePanel("Save TextMesh Pro! Font Asset File", saveDirectory, sourceObject.name, "asset");
00924 
00925                 if (filePath.Length == 0)
00926                     return;
00927 
00928                 Save_Normal_FontAsset(filePath);
00929             }
00930             else if (m_RenderMode >= RenderModes.DistanceField16) // ((RasterModes)m_RenderMode & RasterModes.Raster_Mode_SDF) == RasterModes.Raster_Mode_SDF || m_RenderMode == RenderModes.DistanceFieldAA)
00931             {
00932                 filePath = EditorUtility.SaveFilePanel("Save TextMesh Pro! Font Asset File", saveDirectory, sourceObject.name + " SDF", "asset");
00933 
00934                 if (filePath.Length == 0)
00935                     return;
00936 
00937                 Save_SDF_FontAsset(filePath);
00938             }
00939         }
00940 
00941 
00946         void SaveNewFontAssetWithSameName(Object sourceObject)
00947         {
00948             string filePath;
00949 
00950             // Save new Font Asset and open save file requester at Source Font File location.
00951             string saveDirectory = new FileInfo(AssetDatabase.GetAssetPath(sourceObject)).DirectoryName;
00952 
00953             filePath = EditorUtility.SaveFilePanel("Save TextMesh Pro! Font Asset File", saveDirectory, sourceObject.name, "asset");
00954 
00955             if (filePath.Length == 0)
00956                 return;
00957 
00958             if (m_RenderMode < RenderModes.DistanceField16) // ((int)m_RenderMode & 0x10) == 0x10)
00959             {
00960                 Save_Normal_FontAsset(filePath);
00961             }
00962             else if (m_RenderMode >= RenderModes.DistanceField16) // ((RasterModes)m_RenderMode & RasterModes.Raster_Mode_SDF) == RasterModes.Raster_Mode_SDF || m_RenderMode == RenderModes.DistanceFieldAA)
00963             {
00964                 Save_SDF_FontAsset(filePath);
00965             }
00966         }
