00968 
00969         void Save_Normal_FontAsset(string filePath)
00970         {
00971             filePath = filePath.Substring(0, filePath.Length - 6); // Trim file extension from filePath.
00972 
00973             string dataPath = Application.dataPath;
00974 
00975             if (filePath.IndexOf(dataPath, System.StringComparison.InvariantCultureIgnoreCase) == -1)
00976             {
00977                 Debug.LogError("You're saving the font asset in a directory outside of this project folder. This is not supported. Please select a directory under \"" + dataPath + "\"");
00978                 return;
00979             }
00980 
00981             string relativeAssetPath = filePath.Substring(dataPath.Length - 6);
00982             string tex_DirName = Path.GetDirectoryName(relativeAssetPath);
00983             string tex_FileName = Path.GetFileNameWithoutExtension(relativeAssetPath);
00984             string tex_Path_NoExt = tex_DirName + "/" + tex_FileName;
00985 
00986             // Check if TextMeshPro font asset already exists. If not, create a new one. Otherwise update the existing one.
00987             TMP_FontAsset fontAsset = AssetDatabase.LoadAssetAtPath(tex_Path_NoExt + ".asset", typeof(TMP_FontAsset)) as TMP_FontAsset;
00988             if (fontAsset == null)
00989             {
00990                 //Debug.Log("Creating TextMeshPro font asset!");
00991                 fontAsset = ScriptableObject.CreateInstance<TMP_FontAsset>(); // Create new TextMeshPro Font Asset.
00992                 AssetDatabase.CreateAsset(fontAsset, tex_Path_NoExt + ".asset");
00993 
00994                 //Set Font Asset Type
00995                 fontAsset.fontAssetType = TMP_FontAsset.FontAssetTypes.Bitmap;
00996 
00997                 // Reference to the source font file
00998                 //font_asset.sourceFontFile = font_TTF as Font;
00999 
01000                 // Add FaceInfo to Font Asset
01001                 FaceInfo face = GetFaceInfo(m_FontFaceInfo, 1);
01002                 fontAsset.AddFaceInfo(face);
01003 
01004                 // Add GlyphInfo[] to Font Asset
01005                 TMP_Glyph[] glyphs = GetGlyphInfo(m_FontGlyphInfo, 1);
01006                 fontAsset.AddGlyphInfo(glyphs);
01007 
01008                 // Get and Add Kerning Pairs to Font Asset
01009                 if (m_IncludeKerningPairs)
01010                 {
01011                     string fontFilePath = AssetDatabase.GetAssetPath(m_SourceFontFile);
01012                     KerningTable kerningTable = GetKerningTable(fontFilePath, (int)face.PointSize);
01013                     fontAsset.AddKerningInfo(kerningTable);
01014                 }
01015 
01016 
01017                 // Add Font Atlas as Sub-Asset
01018                 fontAsset.atlas = m_FontAtlas;
01019                 m_FontAtlas.name = tex_FileName + " Atlas";
01020 
01021                 AssetDatabase.AddObjectToAsset(m_FontAtlas, fontAsset);
01022 
01023                 // Create new Material and Add it as Sub-Asset
01024                 Shader default_Shader = Shader.Find("TextMeshPro/Bitmap"); // m_shaderSelection;
01025                 Material tmp_material = new Material(default_Shader);
01026                 tmp_material.name = tex_FileName + " Material";
01027                 tmp_material.SetTexture(ShaderUtilities.ID_MainTex, m_FontAtlas);
01028                 fontAsset.material = tmp_material;
01029 
01030                 AssetDatabase.AddObjectToAsset(tmp_material, fontAsset);
01031 
01032             }
01033             else
01034             {
01035                 // Find all Materials referencing this font atlas.
01036                 Material[] material_references = TMP_EditorUtility.FindMaterialReferences(fontAsset);
01037 
01038                 // Destroy Assets that will be replaced.
01039                 DestroyImmediate(fontAsset.atlas, true);
01040 
01041                 //Set Font Asset Type
01042                 fontAsset.fontAssetType = TMP_FontAsset.FontAssetTypes.Bitmap;
01043 
01044                 // Add FaceInfo to Font Asset
01045                 FaceInfo face = GetFaceInfo(m_FontFaceInfo, 1);
01046                 fontAsset.AddFaceInfo(face);
01047 
01048                 // Add GlyphInfo[] to Font Asset
01049                 TMP_Glyph[] glyphs = GetGlyphInfo(m_FontGlyphInfo, 1);
01050                 fontAsset.AddGlyphInfo(glyphs);
01051 
01052                 // Get and Add Kerning Pairs to Font Asset
01053                 if (m_IncludeKerningPairs)
01054                 {
01055                     string fontFilePath = AssetDatabase.GetAssetPath(m_SourceFontFile);
01056                     KerningTable kerningTable = GetKerningTable(fontFilePath, (int)face.PointSize);
01057                     fontAsset.AddKerningInfo(kerningTable);
01058                 }
01059 
01060                 // Add Font Atlas as Sub-Asset
01061                 fontAsset.atlas = m_FontAtlas;
01062                 m_FontAtlas.name = tex_FileName + " Atlas";
01063 
01064                 // Special handling due to a bug in earlier versions of Unity.
01065                 m_FontAtlas.hideFlags = HideFlags.None;
01066                 fontAsset.material.hideFlags = HideFlags.None;
01067 
01068                 AssetDatabase.AddObjectToAsset(m_FontAtlas, fontAsset);
01069 
01070                 // Assign new font atlas texture to the existing material.
01071                 fontAsset.material.SetTexture(ShaderUtilities.ID_MainTex, fontAsset.atlas);
01072 
01073                 // Update the Texture reference on the Material
01074                 for (int i = 0; i < material_references.Length; i++)
01075                 {
01076                     material_references[i].SetTexture(ShaderUtilities.ID_MainTex, m_FontAtlas);
01077                 }
01078             }
01079 
01080             // Save Font Asset creation settings
01081             m_SelectedFontAsset = fontAsset;
01082             m_LegacyFontAsset = null;
01083             fontAsset.creationSettings = SaveFontCreationSettings();
01084 
01085             AssetDatabase.SaveAssets();
01086 
01087             AssetDatabase.ImportAsset(AssetDatabase.GetAssetPath(fontAsset));  // Re-import font asset to get the new updated version.
01088 
01089             //EditorUtility.SetDirty(font_asset);
01090             fontAsset.ReadFontDefinition();
01091 
01092             AssetDatabase.Refresh();
01093 
01094             m_FontAtlas = null;
01095 
01096             // NEED TO GENERATE AN EVENT TO FORCE A REDRAW OF ANY TEXTMESHPRO INSTANCES THAT MIGHT BE USING THIS FONT ASSET
01097             TMPro_EventManager.ON_FONT_PROPERTY_CHANGED(true, fontAsset);
01098         }
01099 
