01101         void Save_SDF_FontAsset(string filePath)
01102         {
01103             filePath = filePath.Substring(0, filePath.Length - 6); // Trim file extension from filePath.
01104 
01105             string dataPath = Application.dataPath;
01106 
01107             if (filePath.IndexOf(dataPath, System.StringComparison.InvariantCultureIgnoreCase) == -1)
01108             {
01109                 Debug.LogError("You're saving the font asset in a directory outside of this project folder. This is not supported. Please select a directory under \"" + dataPath + "\"");
01110                 return;
01111             }
01112 
01113             string relativeAssetPath = filePath.Substring(dataPath.Length - 6);
01114             string tex_DirName = Path.GetDirectoryName(relativeAssetPath);
01115             string tex_FileName = Path.GetFileNameWithoutExtension(relativeAssetPath);
01116             string tex_Path_NoExt = tex_DirName + "/" + tex_FileName;
01117 
01118 
01119             // Check if TextMeshPro font asset already exists. If not, create a new one. Otherwise update the existing one.
01120             TMP_FontAsset fontAsset = AssetDatabase.LoadAssetAtPath<TMP_FontAsset>(tex_Path_NoExt + ".asset");
01121             if (fontAsset == null)
01122             {
01123                 //Debug.Log("Creating TextMeshPro font asset!");
01124                 fontAsset = ScriptableObject.CreateInstance<TMP_FontAsset>(); // Create new TextMeshPro Font Asset.
01125                 AssetDatabase.CreateAsset(fontAsset, tex_Path_NoExt + ".asset");
01126 
01127                 // Reference to the source font file
01128                 //font_asset.sourceFontFile = font_TTF as Font;
01129 
01130                 //Set Font Asset Type
01131                 fontAsset.fontAssetType = TMP_FontAsset.FontAssetTypes.SDF;
01132 
01133                 //if (m_destination_Atlas != null)
01134                 //    m_font_Atlas = m_destination_Atlas;
01135 
01136                 // If using the C# SDF creation mode, we need the scale down factor.
01137                 int scaleDownFactor = 1; // ((RasterModes)m_RenderMode & RasterModes.Raster_Mode_SDF) == RasterModes.Raster_Mode_SDF || m_RenderMode == RenderModes.DistanceFieldAA ? 1 : font_scaledownFactor;
01138 
01139                 // Add FaceInfo to Font Asset
01140                 FaceInfo face = GetFaceInfo(m_FontFaceInfo, scaleDownFactor);
01141                 fontAsset.AddFaceInfo(face);
01142 
01143                 // Add GlyphInfo[] to Font Asset
01144                 TMP_Glyph[] glyphs = GetGlyphInfo(m_FontGlyphInfo, scaleDownFactor);
01145                 fontAsset.AddGlyphInfo(glyphs);
01146 
01147                 // Get and Add Kerning Pairs to Font Asset
01148                 if (m_IncludeKerningPairs)
01149                 {
01150                     string fontFilePath = AssetDatabase.GetAssetPath(m_SourceFontFile);
01151                     KerningTable kerningTable = GetKerningTable(fontFilePath, (int)face.PointSize);
01152                     fontAsset.AddKerningInfo(kerningTable);
01153                 }
01154 
01155                 // Add Line Breaking Rules
01156                 //LineBreakingTable lineBreakingTable = new LineBreakingTable();
01157                 //
01158 
01159                 // Add Font Atlas as Sub-Asset
01160                 fontAsset.atlas = m_FontAtlas;
01161                 m_FontAtlas.name = tex_FileName + " Atlas";
01162 
01163                 AssetDatabase.AddObjectToAsset(m_FontAtlas, fontAsset);
01164 
01165                 // Create new Material and Add it as Sub-Asset
01166                 Shader default_Shader = Shader.Find("TextMeshPro/Distance Field"); //m_shaderSelection;
01167                 Material tmp_material = new Material(default_Shader);
01168 
01169                 tmp_material.name = tex_FileName + " Material";
01170                 tmp_material.SetTexture(ShaderUtilities.ID_MainTex, m_FontAtlas);
01171                 tmp_material.SetFloat(ShaderUtilities.ID_TextureWidth, m_FontAtlas.width);
01172                 tmp_material.SetFloat(ShaderUtilities.ID_TextureHeight, m_FontAtlas.height);
01173 
01174                 int spread = m_Padding + 1;
01175                 tmp_material.SetFloat(ShaderUtilities.ID_GradientScale, spread); // Spread = Padding for Brute Force SDF.
01176 
01177                 tmp_material.SetFloat(ShaderUtilities.ID_WeightNormal, fontAsset.normalStyle);
01178                 tmp_material.SetFloat(ShaderUtilities.ID_WeightBold, fontAsset.boldStyle);
01179 
01180                 fontAsset.material = tmp_material;
01181 
01182                 AssetDatabase.AddObjectToAsset(tmp_material, fontAsset);
01183 
01184             }
01185             else
01186             {
01187                 // Find all Materials referencing this font atlas.
01188                 Material[] material_references = TMP_EditorUtility.FindMaterialReferences(fontAsset);
01189 
01190                 // Destroy Assets that will be replaced.
01191                 DestroyImmediate(fontAsset.atlas, true);
01192 
01193                 //Set Font Asset Type
01194                 fontAsset.fontAssetType = TMP_FontAsset.FontAssetTypes.SDF;
01195 
01196                 int scaleDownFactor = 1; // ((RasterModes)m_RenderMode & RasterModes.Raster_Mode_SDF) == RasterModes.Raster_Mode_SDF || m_RenderMode == RenderModes.DistanceFieldAA ? 1 : font_scaledownFactor;
01197                 // Add FaceInfo to Font Asset  
01198                 FaceInfo face = GetFaceInfo(m_FontFaceInfo, scaleDownFactor);
01199                 fontAsset.AddFaceInfo(face);
01200 
01201                 // Add GlyphInfo[] to Font Asset
01202                 TMP_Glyph[] glyphs = GetGlyphInfo(m_FontGlyphInfo, scaleDownFactor);
01203                 fontAsset.AddGlyphInfo(glyphs);
01204 
01205                 // Get and Add Kerning Pairs to Font Asset
01206                 if (m_IncludeKerningPairs)
01207                 {
01208                     string fontFilePath = AssetDatabase.GetAssetPath(m_SourceFontFile);
01209                     KerningTable kerningTable = GetKerningTable(fontFilePath, (int)face.PointSize);
01210                     fontAsset.AddKerningInfo(kerningTable);
01211                 }
01212 
01213                 // Add Font Atlas as Sub-Asset
01214                 fontAsset.atlas = m_FontAtlas;
01215                 m_FontAtlas.name = tex_FileName + " Atlas";
01216 
01217                 // Special handling due to a bug in earlier versions of Unity.
01218                 m_FontAtlas.hideFlags = HideFlags.None;
01219                 fontAsset.material.hideFlags = HideFlags.None;
01220 
01221                 AssetDatabase.AddObjectToAsset(m_FontAtlas, fontAsset);
01222 
01223                 // Assign new font atlas texture to the existing material.
01224                 fontAsset.material.SetTexture(ShaderUtilities.ID_MainTex, fontAsset.atlas);
01225 
01226                 // Update the Texture reference on the Material
01227                 for (int i = 0; i < material_references.Length; i++)
01228                 {
01229                     material_references[i].SetTexture(ShaderUtilities.ID_MainTex, m_FontAtlas);
01230                     material_references[i].SetFloat(ShaderUtilities.ID_TextureWidth, m_FontAtlas.width);
01231                     material_references[i].SetFloat(ShaderUtilities.ID_TextureHeight, m_FontAtlas.height);
01232 
01233                     int spread = m_Padding + 1;
01234                     material_references[i].SetFloat(ShaderUtilities.ID_GradientScale, spread); // Spread = Padding for Brute Force SDF.
01235 
01236                     material_references[i].SetFloat(ShaderUtilities.ID_WeightNormal, fontAsset.normalStyle);
01237                     material_references[i].SetFloat(ShaderUtilities.ID_WeightBold, fontAsset.boldStyle);
01238                 }
01239             }
01240 
01241             // Saving File for Debug
01242             //var pngData = destination_Atlas.EncodeToPNG();
01243             //File.WriteAllBytes("Assets/Textures/Debug Distance Field.png", pngData);
01244 
01245             // Save Font Asset creation settings
01246             m_SelectedFontAsset = fontAsset;
01247             m_LegacyFontAsset = null;
01248             fontAsset.creationSettings = SaveFontCreationSettings();
01249 
01250             AssetDatabase.SaveAssets();
01251 
01252             AssetDatabase.ImportAsset(AssetDatabase.GetAssetPath(fontAsset));  // Re-import font asset to get the new updated version.
01253 
01254             fontAsset.ReadFontDefinition();
01255 
01256             AssetDatabase.Refresh();
01257 
01258             m_FontAtlas = null;
01259 
01260             // NEED TO GENERATE AN EVENT TO FORCE A REDRAW OF ANY TEXTMESHPRO INSTANCES THAT MIGHT BE USING THIS FONT ASSET
01261             TMPro_EventManager.ON_FONT_PROPERTY_CHANGED(true, fontAsset);
01262         }
01263 
01264 
01269         FontAssetCreationSettings SaveFontCreationSettings()
01270         {
01271             FontAssetCreationSettings settings = new FontAssetCreationSettings();
01272 
01273             //settings.sourceFontFileName = m_SourceFontFile.name;
01274             settings.sourceFontFileGUID = AssetDatabase.AssetPathToGUID(AssetDatabase.GetAssetPath(m_SourceFontFile));
01275             settings.pointSizeSamplingMode = m_PointSizeSamplingMode;
01276             settings.pointSize = m_PointSize;
01277             settings.padding = m_Padding;
01278             settings.packingMode = (int)m_PackingMode;
01279             settings.atlasWidth = m_AtlasWidth;
01280             settings.atlasHeight = m_AtlasHeight;
01281             settings.characterSetSelectionMode = m_CharacterSetSelectionMode;
01282             settings.characterSequence = m_CharacterSequence;
01283             settings.referencedFontAssetGUID = AssetDatabase.AssetPathToGUID(AssetDatabase.GetAssetPath(m_ReferencedFontAsset));
01284             settings.referencedTextAssetGUID = AssetDatabase.AssetPathToGUID(AssetDatabase.GetAssetPath(m_CharacterList));
01285             settings.fontStyle = (int)m_FontStyle;
01286             settings.fontStyleModifier = m_FontStyleValue;
01287             settings.renderMode = (int)m_RenderMode;
01288             settings.includeFontFeatures = m_IncludeKerningPairs;
01289 
01290             return settings;
01291         }
01292 
01297         void LoadFontCreationSettings(FontAssetCreationSettings settings)
01298         {
01299             m_SourceFontFile = AssetDatabase.LoadAssetAtPath<Font>(AssetDatabase.GUIDToAssetPath(settings.sourceFontFileGUID));
01300             m_PointSizeSamplingMode  = settings.pointSizeSamplingMode;
01301             m_PointSize = settings.pointSize;
01302             m_Padding = settings.padding;
01303             m_PackingMode = (FontPackingModes)settings.packingMode;
01304             m_AtlasWidth = settings.atlasWidth;
01305             m_AtlasHeight = settings.atlasHeight;
01306             m_CharacterSetSelectionMode = settings.characterSetSelectionMode;
01307             m_CharacterSequence = settings.characterSequence;
01308             m_ReferencedFontAsset = AssetDatabase.LoadAssetAtPath<TMP_FontAsset>(AssetDatabase.GUIDToAssetPath(settings.referencedFontAssetGUID));
01309             m_CharacterList = AssetDatabase.LoadAssetAtPath<TextAsset>(AssetDatabase.GUIDToAssetPath(settings.referencedTextAssetGUID));
01310             m_FontStyle = (FaceStyles)settings.fontStyle;
01311             m_FontStyleValue = settings.fontStyleModifier;
01312             m_RenderMode = (RenderModes)settings.renderMode;
01313             m_IncludeKerningPairs = settings.includeFontFeatures;
01314         }
