01321         void SaveCreationSettingsToEditorPrefs(FontAssetCreationSettings settings)
01322         {
01323             // Create new list if one does not already exist
01324             if (m_FontAssetCreationSettingsContainer == null)
01325             {
01326                 m_FontAssetCreationSettingsContainer = new FontAssetCreationSettingsContainer();
01327                 m_FontAssetCreationSettingsContainer.fontAssetCreationSettings = new List<FontAssetCreationSettings>();
01328             }
01329 
01330             // Add new creation settings to the list
01331             m_FontAssetCreationSettingsContainer.fontAssetCreationSettings.Add(settings);
01332 
01333             // Since list should only contain the most 4 recent settings, we remove the first element if list exceeds 4 elements.
01334             if (m_FontAssetCreationSettingsContainer.fontAssetCreationSettings.Count > 4)
01335                 m_FontAssetCreationSettingsContainer.fontAssetCreationSettings.RemoveAt(0);
01336 
01337             m_FontAssetCreationSettingsCurrentIndex = m_FontAssetCreationSettingsContainer.fontAssetCreationSettings.Count - 1;
01338 
01339             // Serialize list to JSON
01340             string serializedSettings = JsonUtility.ToJson(m_FontAssetCreationSettingsContainer, true);
01341 
01342             EditorPrefs.SetString(k_FontAssetCreationSettingsContainerKey, serializedSettings);
01343         }
