01345         void DrawPreview()
01346         {
01347             Rect pixelRect;
01348             if (position.width > position.height && position.width > k_TwoColumnControlsWidth)
01349             {
01350                 float minSide = Mathf.Min(position.height - 15f, position.width - k_TwoColumnControlsWidth);
01351 
01352                 EditorGUILayout.BeginVertical(EditorStyles.helpBox, GUILayout.MaxWidth(minSide));
01353 
01354                 pixelRect = GUILayoutUtility.GetRect(minSide, minSide, GUILayout.ExpandHeight(false), GUILayout.ExpandWidth(false));
01355             }
01356             else
01357             {
01358                 EditorGUILayout.BeginVertical(EditorStyles.helpBox);
01359 
01360                 pixelRect = GUILayoutUtility.GetAspectRect(1f);
01361             }
01362             
01363             if (m_FontAtlas != null)
01364             {
01365                 EditorGUI.DrawTextureAlpha(pixelRect, m_FontAtlas, ScaleMode.StretchToFill);
01366             }
01367             else if (m_SavedFontAtlas != null)
01368             {
01369                 EditorGUI.DrawTextureAlpha(pixelRect, m_SavedFontAtlas, ScaleMode.StretchToFill);
01370             }
01371 
01372             EditorGUILayout.EndVertical();
01373         }
