01376         // Convert from FT_FaceInfo to FaceInfo
01377         static FaceInfo GetFaceInfo(FT_FaceInfo ftFace, int scaleFactor)
01378         {
01379             FaceInfo face = new FaceInfo();
01380 
01381             face.Name = ftFace.name;
01382             face.PointSize = (float)ftFace.pointSize / scaleFactor;
01383             face.Padding = ftFace.padding / scaleFactor;
01384             face.LineHeight = ftFace.lineHeight / scaleFactor;
01385             face.CapHeight = 0;
01386             face.Baseline = 0;
01387             face.Ascender = ftFace.ascender / scaleFactor;
01388             face.Descender = ftFace.descender / scaleFactor;
01389             face.CenterLine = ftFace.centerLine / scaleFactor;
01390             face.Underline = ftFace.underline / scaleFactor;
01391             face.UnderlineThickness = ftFace.underlineThickness == 0 ? 5 : ftFace.underlineThickness / scaleFactor; // Set Thickness to 5 if TTF value is Zero.
01392             face.strikethrough = (face.Ascender + face.Descender) / 2.75f;
01393             face.strikethroughThickness = face.UnderlineThickness;
01394             face.SuperscriptOffset = face.Ascender;
01395             face.SubscriptOffset = face.Underline;
01396             face.SubSize = 0.5f;
01397             //face.CharacterCount = ft_face.characterCount;
01398             face.AtlasWidth = ftFace.atlasWidth / scaleFactor;
01399             face.AtlasHeight = ftFace.atlasHeight / scaleFactor;
01400 
01401             return face;
01402         }
01403 
01404 
01405         // Convert from FT_GlyphInfo[] to GlyphInfo[]
01406         TMP_Glyph[] GetGlyphInfo(FT_GlyphInfo[] ftGlyphs, int scaleFactor)
01407         {
01408             List<TMP_Glyph> glyphs = new List<TMP_Glyph>();
01409             List<int> kerningSet = new List<int>();
01410 
01411             for (int i = 0; i < ftGlyphs.Length; i++)
01412             {
01413                 TMP_Glyph g = new TMP_Glyph();
01414 
01415                 g.id = ftGlyphs[i].id;
01416                 g.x = ftGlyphs[i].x / scaleFactor;
01417                 g.y = ftGlyphs[i].y / scaleFactor;
01418                 g.width = ftGlyphs[i].width / scaleFactor;
01419                 g.height = ftGlyphs[i].height / scaleFactor;
01420                 g.xOffset = ftGlyphs[i].xOffset / scaleFactor;
01421                 g.yOffset = ftGlyphs[i].yOffset / scaleFactor;
01422                 g.xAdvance = ftGlyphs[i].xAdvance / scaleFactor;
01423 
01424                 // Filter out characters with missing glyphs.
01425                 if (g.x == -1)
01426                     continue;
01427 
01428                 glyphs.Add(g);
01429                 kerningSet.Add(g.id);
01430             }
01431 
01432             m_KerningSet = kerningSet.ToArray();
01433 
01434             return glyphs.ToArray();
01435         }
01436 
01437 
01438         // Get Kerning Pairs
01439         public KerningTable GetKerningTable(string fontFilePath, int pointSize)
01440         {
01441             KerningTable kerningInfo = new KerningTable();
01442             kerningInfo.kerningPairs = new List<KerningPair>();
01443 
01444             // Temporary Array to hold the kerning pairs from the Native Plug-in.
01445             FT_KerningPair[] kerningPairs = new FT_KerningPair[7500];
01446 
01447             int kpCount = TMPro_FontPlugin.FT_GetKerningPairs(fontFilePath, m_KerningSet, m_KerningSet.Length, kerningPairs);
01448 
01449             for (int i = 0; i < kpCount; i++)
01450             {
01451                 // Proceed to add each kerning pairs.
01452                 KerningPair kp = new KerningPair((uint)kerningPairs[i].ascII_Left, (uint)kerningPairs[i].ascII_Right, kerningPairs[i].xAdvanceOffset * pointSize);
01453 
01454                 // Filter kerning pairs to avoid duplicates
01455                 int index = kerningInfo.kerningPairs.FindIndex(item => item.firstGlyph == kp.firstGlyph && item.secondGlyph == kp.secondGlyph);
01456 
01457                 if (index == -1)
01458                     kerningInfo.kerningPairs.Add(kp);
01459                 else
01460                     if (!TMP_Settings.warningsDisabled) Debug.LogWarning("Kerning Key for [" + kp.firstGlyph + "] and [" + kp.secondGlyph + "] is a duplicate.");
01461 
01462             }
01463 
01464             return kerningInfo;
01465         }
01466     }
