00232 
00233         public void AddKerningPair()
00234         {
00235             if (kerningPairs.Count == 0)
00236             {
00237                 kerningPairs.Add(new KerningPair(0, 0, 0));
00238             }
00239             else
00240             {
00241                 uint left = kerningPairs.Last().firstGlyph;
00242                 uint right = kerningPairs.Last().secondGlyph;
00243                 float xoffset = kerningPairs.Last().xOffset;
00244 
00245                 kerningPairs.Add(new KerningPair(left, right, xoffset));
00246             }
00247         }
00248 
00249 
00257         public int AddKerningPair(uint first, uint second, float offset)
00258         {
00259             int index = kerningPairs.FindIndex(item => item.firstGlyph == first && item.secondGlyph == second);
00260 
00261             if (index == -1)
00262             {
00263                 kerningPairs.Add(new KerningPair(first, second, offset));
00264                 return 0;
00265             }
00266 
00267             // Return -1 if Kerning Pair already exists.
00268             return -1;
00269         }
00270 
00279         public int AddGlyphPairAdjustmentRecord(uint first, GlyphValueRecord firstAdjustments, uint second, GlyphValueRecord secondAdjustments)
00280         {
00281             int index = kerningPairs.FindIndex(item => item.firstGlyph == first && item.secondGlyph == second);
00282 
00283             if (index == -1)
00284             {
00285                 kerningPairs.Add(new KerningPair(first, firstAdjustments, second, secondAdjustments));
00286                 return 0;
