00317     public static class TMP_FontUtilities
00318     {
00319         private static List<int> k_searchedFontAssets;
00320 
00328         public static TMP_FontAsset SearchForGlyph(TMP_FontAsset font, int character, out TMP_Glyph glyph)
00329         {
00330             if (k_searchedFontAssets == null)
00331                 k_searchedFontAssets = new List<int>();
00332 
00333             k_searchedFontAssets.Clear();
00334 
00335             return SearchForGlyphInternal(font, character, out glyph);
00336         }
00337 
00338 
00346         public static TMP_FontAsset SearchForGlyph(List<TMP_FontAsset> fonts, int character, out TMP_Glyph glyph)
00347         {
00348             return SearchForGlyphInternal(fonts, character, out glyph);
00349         }
00350 
00351 
00352         private static TMP_FontAsset SearchForGlyphInternal (TMP_FontAsset font, int character, out TMP_Glyph glyph)
00353         {
00354             glyph = null;
00355 
00356             if (font == null) return null;
00357 
00358             if (font.characterDictionary.TryGetValue(character, out glyph))
00359             {
00360                 return font;
00361             }
00362             else if (font.fallbackFontAssets != null && font.fallbackFontAssets.Count > 0)
00363             {
00364                 for (int i = 0; i < font.fallbackFontAssets.Count && glyph == null; i++)
00365                 {
00366                     TMP_FontAsset temp = font.fallbackFontAssets[i];
00367                     if (temp == null) continue;
00368 
00369                     int id = temp.GetInstanceID();
00370 
00371                     // Skip over the fallback font asset in the event it is null or if already searched.
00372                     if (k_searchedFontAssets.Contains(id)) continue;
00373 
00374                     // Add to list of font assets already searched.
00375                     k_searchedFontAssets.Add(id);
00376 
00377                     temp = SearchForGlyphInternal(temp, character, out glyph);
00378 
00379                     if (temp != null)
00380                         return temp;
00381                 }
00382             }
00383 
00384             return null;
00385         }
00386 
00387 
00388         private static TMP_FontAsset SearchForGlyphInternal(List<TMP_FontAsset> fonts, int character, out TMP_Glyph glyph)
00389         {
00390             glyph = null;
00391 
00392             if (fonts != null && fonts.Count > 0)
00393             {
00394                 for (int i = 0; i < fonts.Count; i++)
00395                 {
00396                     TMP_FontAsset fontAsset = SearchForGlyphInternal(fonts[i], character, out glyph);
00397 
00398                     if (fontAsset != null)
00399                         return fontAsset;
00400                 }
00401             }
00402 
00403             return null;
00404         }
00405     }
