00068         protected override void Awake()
00069         {
00070             //Debug.Log("Awake() called on Object ID " + GetInstanceID());
00071 
00072             #if UNITY_EDITOR
00073             // Special handling for TMP Settings and importing Essential Resources
00074             if (TMP_Settings.instance == null)
00075             {
00076                 if (m_isWaitingOnResourceLoad == false)
00077                     TMPro_EventManager.RESOURCE_LOAD_EVENT.Add(ON_RESOURCES_LOADED);
00078 
00079                 m_isWaitingOnResourceLoad = true;
00080                 return;
00081             }
00082             #endif
00083 
00084             // Cache Reference to the Mesh Renderer.
00085             m_renderer = GetComponent<Renderer>();
00086             if (m_renderer == null)
00087                 m_renderer = gameObject.AddComponent<Renderer>();
00088 
00089 
00090             // Make sure we have a CanvasRenderer for compatibility reasons and hide it
00091             if (this.canvasRenderer != null)
00092                 this.canvasRenderer.hideFlags = HideFlags.HideInInspector;
00093             else
00094             {
00095                 CanvasRenderer canvasRenderer = gameObject.AddComponent<CanvasRenderer>();
00096                 canvasRenderer.hideFlags = HideFlags.HideInInspector;
00097             }
00098 
00099 
00100             // Cache Reference to RectTransform
00101             m_rectTransform = this.rectTransform;
00102 
00103 
00104             // Cache Reference to the transform;
00105             m_transform = this.transform;
00106 
00107 
00108             // Cache a reference to the Mesh Filter.
00109             m_meshFilter = GetComponent<MeshFilter>();
00110             if (m_meshFilter == null)
00111                 m_meshFilter = gameObject.AddComponent<MeshFilter>();
00112 
00113 
00114             // Cache a reference to our mesh.
00115             if (m_mesh == null)
00116             {
00117                 //Debug.Log("Creating new mesh.");
00118                 m_mesh = new Mesh();
00119                 m_mesh.hideFlags = HideFlags.HideAndDontSave;
00120 
00121                 m_meshFilter.mesh = m_mesh;
00122                 //m_mesh.bounds = new Bounds(transform.position, new Vector3(1000, 1000, 0));
00123             }
00124             m_meshFilter.hideFlags = HideFlags.HideInInspector;
00125 
00126             // Load TMP Settings for new text object instances.
00127             LoadDefaultSettings();
00128 
00129             // Load the font asset and assign material to renderer.
00130             LoadFontAsset();
00131 
00132             // Load Default TMP StyleSheet
00133             TMP_StyleSheet.LoadDefaultStyleSheet();
00134 
00135             // Allocate our initial buffers.
00136             if (m_char_buffer == null)
00137                 m_char_buffer = new int[m_max_characters];
00138 
