00304         // Event received when custom material editor properties are changed.
00305         void ON_MATERIAL_PROPERTY_CHANGED(bool isChanged, Material mat)
00306         {
00307             //Debug.Log("ON_MATERIAL_PROPERTY_CHANGED event received. Targeted Material is: " + mat.name + "  m_sharedMaterial: " + m_sharedMaterial.name + "  m_renderer.sharedMaterial: " + m_renderer.sharedMaterial);         
00308 
00309             if (m_renderer.sharedMaterial == null)
00310             {
00311                 if (m_fontAsset != null)
00312                 {
00313                     m_renderer.sharedMaterial = m_fontAsset.material;
00314                     Debug.LogWarning("No Material was assigned to " + name + ". " + m_fontAsset.material.name + " was assigned.", this);
00315                 }
00316                 else
00317                     Debug.LogWarning("No Font Asset assigned to " + name + ". Please assign a Font Asset.", this);
00318             }
00319 
00320             if (m_fontAsset.atlas.GetInstanceID() != m_renderer.sharedMaterial.GetTexture(ShaderUtilities.ID_MainTex).GetInstanceID())
00321             {
00322                 m_renderer.sharedMaterial = m_sharedMaterial;
00323                 //m_renderer.sharedMaterial = m_fontAsset.material;
00324                 Debug.LogWarning("Font Asset Atlas doesn't match the Atlas in the newly assigned material. Select a matching material or a different font asset.", this);
00325             }
00326 
00327             if (m_renderer.sharedMaterial != m_sharedMaterial) //    || m_renderer.sharedMaterials.Contains(mat))
00328             {
00329                 //Debug.Log("ON_MATERIAL_PROPERTY_CHANGED Called on Target ID: " + GetInstanceID() + ". Previous Material:" + m_sharedMaterial + "  New Material:" + m_renderer.sharedMaterial); // on Object ID:" + GetInstanceID() + ". m_sharedMaterial: " + m_sharedMaterial.name + "  m_renderer.sharedMaterial: " + m_renderer.sharedMaterial.name);         
00330                 m_sharedMaterial = m_renderer.sharedMaterial;
00331             }
00332 
00333             m_padding = GetPaddingForMaterial();
00334             //m_sharedMaterialHashCode = TMP_TextUtilities.GetSimpleHashCode(m_sharedMaterial.name);
00335 
00336             UpdateMask();
00337             UpdateEnvMapMatrix();
00338             m_havePropertiesChanged = true;
00339             SetVerticesDirty();
00340         }
