00374         // Event to Track Material Changed resulting from Drag-n-drop.
00375         void ON_DRAG_AND_DROP_MATERIAL(GameObject obj, Material currentMaterial, Material newMaterial)
00376         {
00377             //Debug.Log("Drag-n-Drop Event - Receiving Object ID " + GetInstanceID()); // + ". Target Object ID " + obj.GetInstanceID() + ".  New Material is " + mat.name + " with ID " + mat.GetInstanceID() + ". Base Material is " + m_baseMaterial.name + " with ID " + m_baseMaterial.GetInstanceID());
00378 
00379             // Check if event applies to this current object
00380             #if UNITY_2018_2_OR_NEWER
00381             if (obj == gameObject || UnityEditor.PrefabUtility.GetCorrespondingObjectFromSource(gameObject) == obj)
00382             #else
00383             if (obj == gameObject || UnityEditor.PrefabUtility.GetPrefabParent(gameObject) == obj)
00384             #endif
00385             {
00386                 UnityEditor.Undo.RecordObject(this, "Material Assignment");
00387                 UnityEditor.Undo.RecordObject(m_renderer, "Material Assignment");
00388 
00389                 m_sharedMaterial = newMaterial;
00390 
00391                 m_padding = GetPaddingForMaterial();
00392                 m_havePropertiesChanged = true;
00393                 SetVerticesDirty();
00394                 SetMaterialDirty();
00395             }
00396         }
