00435             // Function which loads either the default font or a newly assigned font asset. This function also assigned the appropriate material to the renderer.
00436         protected override void LoadFontAsset()
00437         {
00438             //Debug.Log("TextMeshPro LoadFontAsset() has been called."); // Current Font Asset is " + (font != null ? font.name: "Null") );
00439 
00440             ShaderUtilities.GetShaderPropertyIDs(); // Initialize & Get shader property IDs.
00441 
00442             if (m_fontAsset == null)
00443             {
00444                 if (TMP_Settings.defaultFontAsset != null)
00445                     m_fontAsset =TMP_Settings.defaultFontAsset;
00446                 else
00447                     m_fontAsset = Resources.Load<TMP_FontAsset>("Fonts & Materials/LiberationSans SDF");
00448 
00449                 if (m_fontAsset == null)
00450                 {
00451                     Debug.LogWarning("The LiberationSans SDF Font Asset was not found. There is no Font Asset assigned to " + gameObject.name + ".", this);
00452                     return;
00453                 }
00454 
00455                 if (m_fontAsset.characterDictionary == null)
00456                 {
00457                     Debug.Log("Dictionary is Null!");
00458                 }
00459 
00460                 m_renderer.sharedMaterial = m_fontAsset.material;
00461                 m_sharedMaterial = m_fontAsset.material;
00462                 m_sharedMaterial.SetFloat("_CullMode", 0);
00463                 m_sharedMaterial.SetFloat(ShaderUtilities.ShaderTag_ZTestMode, 4);
00464                 m_renderer.receiveShadows = false;
00465                 m_renderer.shadowCastingMode = UnityEngine.Rendering.ShadowCastingMode.Off; // true;
00466                 // Get a Reference to the Shader
00467             }
00468             else
00469             {
00470                 if (m_fontAsset.characterDictionary == null)
00471                 {
00472                     //Debug.Log("Reading Font Definition and Creating Character Dictionary.");
00473                     m_fontAsset.ReadFontDefinition();
00474                 }
00475 
00476                 //Debug.Log("Font Asset name:" + font.material.name);
00477 
00478                 // If font atlas texture doesn't match the assigned material font atlas, switch back to default material specified in the Font Asset.
00479                 if (m_renderer.sharedMaterial == null || m_renderer.sharedMaterial.GetTexture(ShaderUtilities.ID_MainTex) == null || m_fontAsset.atlas.GetInstanceID() != m_renderer.sharedMaterial.GetTexture(ShaderUtilities.ID_MainTex).GetInstanceID())
00480                 {
00481                     m_renderer.sharedMaterial = m_fontAsset.material;
00482                     m_sharedMaterial = m_fontAsset.material; 
00483                 }
00484                 else
00485                 {
00486                     m_sharedMaterial = m_renderer.sharedMaterial;
00487                 }
00488 
00489                 //m_sharedMaterial.SetFloat("_CullMode", 0);
00490                 m_sharedMaterial.SetFloat(ShaderUtilities.ShaderTag_ZTestMode, 4);
00491 
00492                 // Check if we are using the SDF Surface Shader
00493                 if (m_sharedMaterial.passCount == 1)
00494                 {
00495                     m_renderer.receiveShadows = false;
00496                     m_renderer.shadowCastingMode = UnityEngine.Rendering.ShadowCastingMode.Off;
00497                 }
00498 
00499             }
00500 
00501             m_padding = GetPaddingForMaterial();
00502             //m_alignmentPadding = ShaderUtilities.GetFontExtent(m_sharedMaterial);
00503             m_isMaskingEnabled = ShaderUtilities.IsMaskingEnabled(m_sharedMaterial);
00504 
00505 
00506             // Find and cache Underline & Ellipsis characters.
00507             GetSpecialCharacters(m_fontAsset);
00508 
00509 
00510             //m_sharedMaterials.Add(m_sharedMaterial);
00511             //m_sharedMaterialHashCode = TMP_TextUtilities.GetSimpleHashCode(m_sharedMaterial.name);
00512             // Hide Material Editor Component
00513             //m_renderer.sharedMaterial.hideFlags = HideFlags.None;
00514         }
00515 
00516 
00517         void UpdateEnvMapMatrix()
00518         {
00519             if (!m_sharedMaterial.HasProperty(ShaderUtilities.ID_EnvMap) || m_sharedMaterial.GetTexture(ShaderUtilities.ID_EnvMap) == null)
00520                 return;
00521 
00522             //Debug.Log("Updating Env Matrix...");
00523             Vector3 rotation = m_sharedMaterial.GetVector(ShaderUtilities.ID_EnvMatrixRotation);
00524             m_EnvMapMatrix = Matrix4x4.TRS(Vector3.zero, Quaternion.Euler(rotation), Vector3.one);
00525 
00526             m_sharedMaterial.SetMatrix(ShaderUtilities.ID_EnvMatrix, m_EnvMapMatrix);
00527         }
