00530         //
00531         void SetMask(MaskingTypes maskType)
00532         {
00533             switch(maskType)
00534             {
00535                 case MaskingTypes.MaskOff:
00536                     m_sharedMaterial.DisableKeyword(ShaderUtilities.Keyword_MASK_SOFT);
00537                     m_sharedMaterial.DisableKeyword(ShaderUtilities.Keyword_MASK_HARD);
00538                     m_sharedMaterial.DisableKeyword(ShaderUtilities.Keyword_MASK_TEX);
00539                     break;
00540                 case MaskingTypes.MaskSoft:
00541                     m_sharedMaterial.EnableKeyword(ShaderUtilities.Keyword_MASK_SOFT);
00542                     m_sharedMaterial.DisableKeyword(ShaderUtilities.Keyword_MASK_HARD);
00543                     m_sharedMaterial.DisableKeyword(ShaderUtilities.Keyword_MASK_TEX);
00544                     break;
00545                 case MaskingTypes.MaskHard:
00546                     m_sharedMaterial.EnableKeyword(ShaderUtilities.Keyword_MASK_HARD);
00547                     m_sharedMaterial.DisableKeyword(ShaderUtilities.Keyword_MASK_SOFT);
00548                     m_sharedMaterial.DisableKeyword(ShaderUtilities.Keyword_MASK_TEX);
00549                     break;
00550                 //case MaskingTypes.MaskTex:
00551                 //    m_sharedMaterial.EnableKeyword(ShaderUtilities.Keyword_MASK_TEX);
00552                 //    m_sharedMaterial.DisableKeyword(ShaderUtilities.Keyword_MASK_HARD);
00553                 //    m_sharedMaterial.DisableKeyword(ShaderUtilities.Keyword_MASK_SOFT);
00554                 //    break;
00555             }
00556         }
