00575         // Enable Masking in the Shader
00576         void EnableMasking()
00577         {
00578             if (m_sharedMaterial.HasProperty(ShaderUtilities.ID_ClipRect))
00579             {
00580                 m_sharedMaterial.EnableKeyword(ShaderUtilities.Keyword_MASK_SOFT);
00581                 m_sharedMaterial.DisableKeyword(ShaderUtilities.Keyword_MASK_HARD);
00582                 m_sharedMaterial.DisableKeyword(ShaderUtilities.Keyword_MASK_TEX);
00583 
00584                 m_isMaskingEnabled = true;
00585                 UpdateMask();
00586             }
00587         }
00588 
00589 
00590         // Enable Masking in the Shader
00591         void DisableMasking()
00592         {
00593             if (m_sharedMaterial.HasProperty(ShaderUtilities.ID_ClipRect))
00594             {
00595                 m_sharedMaterial.DisableKeyword(ShaderUtilities.Keyword_MASK_SOFT);
00596                 m_sharedMaterial.DisableKeyword(ShaderUtilities.Keyword_MASK_HARD);
00597                 m_sharedMaterial.DisableKeyword(ShaderUtilities.Keyword_MASK_TEX);
00598 
00599                 m_isMaskingEnabled = false;
00600                 UpdateMask();
00601             }
00602         }
