00679         // Function called internally when a new material is assigned via the fontMaterial property.
00680         protected override Material GetMaterial(Material mat)
00681         {
00682             // Check in case Object is disabled. If so, we don't have a valid reference to the Renderer.
00683             // This can occur when the Duplicate Material Context menu is used on an inactive object.
00684             //if (m_renderer == null)
00685             //    m_renderer = GetComponent<Renderer>();
00686 
00687             // Create Instance Material only if the new material is not the same instance previously used.
00688             if (m_fontMaterial == null || m_fontMaterial.GetInstanceID() != mat.GetInstanceID())
00689                 m_fontMaterial = CreateMaterialInstance(mat);
00690 
00691             m_sharedMaterial = m_fontMaterial;
00692 
00693             m_padding = GetPaddingForMaterial();
00694 
00695             SetVerticesDirty();
00696             SetMaterialDirty();
00697 
00698             return m_sharedMaterial;
00699         }
