00706         protected override Material[] GetMaterials(Material[] mats)
00707         {
00708             int materialCount = m_textInfo.materialCount;
00709 
00710             if (m_fontMaterials == null)
00711                 m_fontMaterials = new Material[materialCount];
00712             else if (m_fontMaterials.Length != materialCount)
00713                 TMP_TextInfo.Resize(ref m_fontMaterials, materialCount, false);
00714 
00715             // Get instances of the materials
00716             for (int i = 0; i < materialCount; i++)
00717             {
00718                 if (i == 0)
00719                     m_fontMaterials[i] = fontMaterial;
00720                 else
00721                     m_fontMaterials[i] = m_subTextObjects[i].material;
00722             }
00723 
00724             m_fontSharedMaterials = m_fontMaterials;
00725 
00726             return m_fontMaterials;
00727         }
