00770 
00774         protected override void SetSharedMaterials(Material[] materials)
00775         {
00776             int materialCount = m_textInfo.materialCount;
00777 
00778             // Check allocation of the fontSharedMaterials array.
00779             if (m_fontSharedMaterials == null)
00780                 m_fontSharedMaterials = new Material[materialCount];
00781             else if (m_fontSharedMaterials.Length != materialCount)
00782                 TMP_TextInfo.Resize(ref m_fontSharedMaterials, materialCount, false);
00783 
00784             // Only assign as many materials as the text object contains.
00785             for (int i = 0; i < materialCount; i++)
00786             {
00787                 Texture mat_MainTex = materials[i].GetTexture(ShaderUtilities.ID_MainTex);
00788 
00789                 if (i == 0)
00790                 {
00791                     // Only assign new material if the font atlas textures match.
00792                     if ( mat_MainTex == null || mat_MainTex.GetInstanceID() != m_sharedMaterial.GetTexture(ShaderUtilities.ID_MainTex).GetInstanceID())
00793                         continue;
00794 
00795                     m_sharedMaterial = m_fontSharedMaterials[i] = materials[i];
00796                     m_padding = GetPaddingForMaterial(m_sharedMaterial);
00797                 }
00798                 else
00799                 {
00800                     // Only assign new material if the font atlas textures match.
00801                     if (mat_MainTex == null || mat_MainTex.GetInstanceID() != m_subTextObjects[i].sharedMaterial.GetTexture(ShaderUtilities.ID_MainTex).GetInstanceID())
00802                         continue;
00803 
00804                     // Only assign a new material if none were specified in the text input.
00805                     if (m_subTextObjects[i].isDefaultMaterial)
00806                         m_subTextObjects[i].sharedMaterial = m_fontSharedMaterials[i] = materials[i];
00807                 }
00808             }
00809         }
