00865         // Sets the Render Queue and Ztest mode 
00866         protected override void SetShaderDepth()
00867         {
00868             if (m_isOverlay)
00869             {
00870                 // Changing these properties results in an instance of the material
00871                 m_sharedMaterial.SetFloat(ShaderUtilities.ShaderTag_ZTestMode, 0);
00872                 //m_renderer.material.SetFloat("_ZTestMode", 8);
00873                 m_renderer.material.renderQueue = 4000;
00874 
00875                 m_sharedMaterial = m_renderer.material;
00876                 //Debug.Log("Text set to Overlay mode.");
00877             }
00878             else
00879             {
00880                 // Should this use an instanced material?
00881                 m_sharedMaterial.SetFloat(ShaderUtilities.ShaderTag_ZTestMode, 4);
00882                 m_renderer.material.renderQueue = -1;
00883                 
00884                 m_sharedMaterial = m_renderer.material;
00885                 //Debug.Log("Text set to Normal mode.");
00886             }
00887         }
